style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

/* ############################################################################## */
/* ################################################################################# GLOBALS */
/* ############################################################################## */

.dashboard-left{
width: 290px !important;
margin-left: -40px !important;
}

.content-main{
width: 625px !important;
margin-right: 10px !important;
}

.datepicker th, .datepicker td.day, .datepicker td.dow{
border: 1px solid #161616 !important;
}

.datepicker .month, .datepicker .year{
border: 1px solid #161616 !important;
margin-left: 0px !important;
}

.datepicker th:hover, .datepicker td.day:not(.disabled):hover, .datepicker td.dow:hover, .datepicker .month:not(.disabled):hover, .datepicker .year:not(.disabled):hover{
background: #242424!important;
color: #a3a3a3!important;
border-top: 1px solid #323232!important;
border-left: 1px solid #323232!important;
border-bottom: 1px solid #020202 !important;
border-right: 1px solid #020202 !important;
}

.datepicker .selected, .datepicker .range{
background: #121212!important;
color: #a3a3a3!important;
border-top: 1px solid #323232!important;
border-left: 1px solid #323232!important;
border-bottom: 1px solid #020202 !important;
border-right: 1px solid #020202 !important;
}

.datepicker .disabled{
color: #020202!important;
}

.datepicker .today{
background: #646464 !important;
}

/* ############################################################################## */
/* ################################################################################# GLOBALS : TOOLTIPS */
/* ############################################################################## */

.tooltip-inner{
background: #323232 !important;
border-top: 1px solid #484848!important;
border-left: 1px solid #484848!important;
border-bottom: 1px solid #161616 !important;
border-right: 1px solid #161616 !important;
padding: 2px !important;
padding-left: 5px !important;
padding-right: 7px !important;
color: #c4c4c4 !important;
}

/* ############################################################################## */
/* ################################################################################# GLOBALS : BUTTONS */
/* ############################################################################## */

.btn:hover .follow-text, .following-text{
color: #24a424 !important;
}

.btn:hover .unfollow-text, .blocked-text{
color: #a42424 !important;
}

.btn:hover .unblock-text, .pending-text, .cancel-text{
color: #2424a4 !important;
}

.btn{
outline: none !important;
box-shadow: none !important;
}

.small-follow-btn{
background: #161616 !important;
border-top: 1px solid #242424!important;
border-left: 1px solid #242424!important;
border-bottom: 1px solid #020202 !important;
border-right: 1px solid #020202 !important;
color: #969696 !important;
}

.small-follow-btn:hover{
color: #24a424 !important;
}

.small-follow-btn:active{
background: #0c0c0c !important;
border-top: 1px solid #020202 !important;
border-left: 1px solid #020202 !important;
border-bottom: 1px solid #242424 !important;
border-right: 1px solid #242424 !important;
}

.js-follow-btn{
background: #161616 !important;
border-top: 1px solid #242424!important;
border-left: 1px solid #242424!important;
border-bottom: 1px solid #020202 !important;
border-right: 1px solid #020202 !important;
color: #969696 !important;
}

.js-follow-btn:hover{
color: #24a424 !important;
}

.js-follow-btn:active{
background: #0c0c0c !important;
border-top: 1px solid #020202 !important;
border-left: 1px solid #020202 !important;
border-bottom: 1px solid #242424 !important;
border-right: 1px solid #242424 !important;
}

.flex-table-btn.js-submit{
background: #242424!important;
border-top: 1px solid #323232!important;
border-left: 1px solid #323232!important;
border-bottom: 1px solid #020202 !important;
border-right: 1px solid #020202 !important;
color: #969696 !important;
}

.flex-table-btn.js-submit:hover{
color: #24a424 !important;
}

.flex-table-btn.js-submit:active{
background: #161616!important;
border-top: 1px solid #020202 !important;
border-left: 1px solid #020202 !important;
border-bottom: 1px solid #242424 !important;
border-right: 1px solid #242424 !important;
}


.primary-btn.submit, .ProfileNav-item .UserActions .btn, .find-friends-sources .btn, .dm-conversation-list .mark-read .btn, .dm-conversation-list .mark-read-confirm .btn{
background: #161616 !important;
border-top: 1px solid #242424!important;
border-left: 1px solid #242424!important;
border-bottom: 1px solid #020202 !important;
border-right: 1px solid #020202 !important;
color: #969696 !important;
}

.primary-btn.submit:hover, .ProfileNav-item .UserActions .btn:hover, .find-friends-sources .btn:hover, .dm-conversation-list .mark-read .btn:hover, .dm-conversation-list .mark-read-confirm .btn:hover{
color: #e3e3e3!important;
}

.primary-btn.submit:active, .ProfileNav-item .UserActions .btn:active, .find-friends-sources .btn:active, .dm-conversation-list .mark-read .btn:active, .dm-conversation-list .mark-read-confirm .btn:active{
background: #101010!important;
border-top: 1px solid #020202 !important;
border-left: 1px solid #020202 !important;
border-bottom: 1px solid #242424 !important;
border-right: 1px solid #242424 !important;
}

/* ############################################################################## */
/* ################################################################################# GLOBALS : DROPDOWN MENU */
/* ############################################################################## */

.dropdown-caret *, .caret-inner, .caret-outter, .inline-reply-caret{
border: none !important;
}

.dropdown-menu{
background: #161616 !important;
border-top: 1px solid #323232!important;
border-left: 1px solid #323232!important;
border-bottom: 1px solid #020202!important;
border-right: 1px solid #020202!important;
}

.dropdown-menu:hover{
box-shadow: 0px 0px 10px #c4c4c4 !important;
}

.dropdown-menu .typeahead-items{
border: none !important;
padding-bottom:4px !important;
}

.dropdown-menu .typeahead-user-item-info{
padding-bottom: 2px !important;
}

.dropdown-menu .js-nav .Icon--verified{
color: #00c3f4!important;
}

.dropdown-menu .js-nav s, .dropdown-menu .js-nav b{
color: #646464 !important;
}

.dropdown-menu .js-nav .fullname, .dropdown-menu li:hover .js-nav s, .dropdown-menu li:hover .js-nav b{
color: #969696 !important;
}

.dropdown-menu li:hover .js-nav .fullname{
color: #e3e3e3 !important;
}

.dropdown-menu ul li *{
background: transparent !important;
}

.dropdown-menu ul li{
border: 1px solid #161616!important;
margin-left: 2px !important;
margin-right: 2px !important;
}

.dropdown-menu ul li a, .dropdown-menu ul li button, .dropdown-menu ul li .fullname{
color: #646464 !important;
}

.dropdown-menu ul li:hover a, .dropdown-menu ul li:hover button, .dropdown-menu ul li:hover .fullname{
color: #c3c3c3!important;
}

.dropdown-menu ul li:not(.geo-not-enabled-yet):hover{
background: #101010!important;
border-top: 1px solid #020202 !important;
border-left: 1px solid #020202 !important;
border-bottom: 1px solid #242424 !important;
border-right: 1px solid #242424 !important;
margin-left: 2px !important;
margin-right: 2px !important;
}

.dropdown-menu .dropdown-divider, .dropdown-menu .dropdown-divider:hover{
border: none !important;
border-top: 1px solid #242424 !important;
margin: none !important;
}

.dropdown-menu .geo-not-enabled-yet h2{
color: #a3a3a3 !important;
}

.dropdown-menu .geo-not-enabled-yet p{
color: #646464 !important;
}

.dropdown-menu .geo-not-enabled-yet button.geo-turn-on{
background: #242424!important;
border-top: 1px solid #323232 !important;
border-left: 1px solid #323232 !important;
border-bottom: 1px solid #080808 !important;
border-right: 1px solid #080808 !important;
color: #969696 !important;
}

.dropdown-menu .geo-not-enabled-yet button.geo-turn-on:active{
border-top: 1px solid #080808 !important;
border-left: 1px solid #080808 !important;
border-bottom: 1px solid #323232 !important;
border-right: 1px solid #323232 !important;
}

.wrapper-permalink .tweet .action-more-container .dropdown-menu, .js-stream-tweet .action-more-container .dropdown-menu{
padding: 0px !important;
}

.wrapper-permalink .tweet .action-more-container .dropdown-menu li button, .js-stream-tweet .action-more-container .dropdown-menu li button{
margin-top: -9px !important;
margin-bottom: -9px !important;
border-top: 1px solid #161616!important;
border-bottom: 1px solid #161616!important;
border-left: 1px solid #161616!important;
}

.wrapper-permalink .tweet .action-more-container .dropdown-menu li button:hover, .js-stream-tweet .action-more-container .dropdown-menu li button:hover{
background: #121212 !important;
border-top: 1px solid #020202!important;
border-bottom: 1px solid #323232!important;
border-left: 1px solid #323232!important;
}

.dropdown-menu .typeahead-accounts-shortcut a strong{
color: #969696 !important;
}

.dropdown-menu .typeahead-recent-search-item{
background: #161616 !important;
}

/* ############################################################################## */
/* ################################################################################# NAVBAR */
/* ############################################################################## */

.global-nav, .global-nav-inner{
background-image: url(http://i.imgur.com/9d4RUEq.png) !important;
border: none !important;
}

.global-nav .search-input{
