style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linda-goodman.com")

/* Autor: 7thGuardian */
/* Mail: xsallin@gmail.com */ 

{

body 

{
background: linear-gradient(to bottom,#E4EFC0 0%,#ABBD73 50%, #E4EFC0 100%);
color:#111 !important;
text-align: center !important;
}


table 

{
margin-right: auto !important;
margin-left: auto !important;
border: #E4EFC0  1.7px solid !important;
border-radius: 0.25em;
}

table, tr, td, table.tableThumb 

{
background: linear-gradient(to bottom,#E4EFC0 0%,#ABBD73 200%);
}


font {color:#111 !important;text-decoration:none !important;}




/*Links*/



a:link

{color:#045311 !important;text-decoration:none !important;}
 


a:visited, body_avisited

{color:#852100 !important;text-decoration:none !important;}



a:hover, a:active, body_ahover

{color:#211922 !important;text-decoration:underline !important; }




/*Quotes*/


BLOCKQUOTE, WBR 

{
border:#E1EAE1 15px solid !important;
border-radius: 1em;
background:#E1EAE1  !important;
box-shadow: 1px 3px 5px 3px #555;
}

}
