style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lavabit.com") {
  * {
    font-family: monospace, serif;
    padding: 0.1em;
    font-size: 8px;
    color: #D2D2F7;
    background: #25252E;
  }

  #left_logo {
    display:none
  }

  img[src="/images/logo.gif"] {
    display:none;
  }

  input, select {
    border: solid 1px #D2D2F7;
    padding: 0.4em;
  }

  table table table table{
    border-top: 1px dotted #D2D2F7;
    width: 100%;
    margin-bottom: 10px;
  }

  .gray b { color: #cc0 !important; }
  .gray b small { color: #aaa !important; }
  .gray b a { color: #ff0 !important; }
  .white b { color: #cc0 !important; }
  .white b small { color: #aaa !important; }
  .white b a { color: #ff0 !important; }
}
