style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(file://) {
   textarea:not([disabled]), button:not([disabled]), input[type="text"]:not([disabled]), input:not([type]):not([disabled]), input[type="reset"]:not([disabled]), input[type="submit"]:not([disabled]), input[type="password"]:not([disabled]), input[type="file"]:not([disabled]), input[type="button"]:not([disabled]) {
   background-image: url(data:image/png;base64,) !important;
   background-color: #FFF !important;
   background-repeat: repeat-x !important;
   }
   textarea, button, input[type="text"], input:not([type]), input[type="reset"], input[type="submit"], input[type="password"], input[type="file"], input[type="button"], select {
   border: 1px solid #CCC !important;
   color: #4B4B4C !important;
   }
}
