style.css:
@-moz-document url-prefix("http://hootsuite.com/dashboard"), url-prefix("https://hootsuite.com/dashboard") {
.message .messageContent, .message .messageComment .comment, .g-w .w-e-bb,
.caption, .description, ._commentEntryArea, textarea
 {
text-align: right !important;
font-family: tahoma !important;
direction: rtl !important;
/*[[biggerfont]]*/
}
.message .messageData .messageUserInfo  {
direction: ltr !important;
}
.comment._comment{
font-family:tahoma !important;
}
.message .messageData a.username {
margin-right: 5px;
}
.socialNetwork .socialAvatar .round-30, .message .messageData a.icon, .message .messageData .messageUserImage {
position: relative !important;
float: right !important;
right: 0px !important;
}
.column .column-scroll .messageOptions  {
float:left !important;
border: 0px solid #CCC !important;
width: 110px;
left: 6px !important;
background: none !important;
}
.avatarFacebook .message p.messageContent, .message .messageContent, .message .messageUserImage .corners {
background: none !important;
}
#footer .footerBucket { 
display: none !important;
}
}
