@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rlslog.net") {

/* Hides Comments */
#commentblock, .comments_link { display: none !important; }

/* Hides "Loading" */
#MarketGidComposite731 { display: none !important; }

/* Hides "Ads" */
a[href*="ad."] { display: none !important; }

/* Hides NoScript Place Holder Icons */
[class="__noscriptPlaceholder__ "], .__noscriptPlaceholder__.__noscriptObjectPatchMe__ { display: none !important; }

}
