style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
#blueBar{background-color: green !important;}
a{color: green !important;}
#pageNav .navLink, #pageNav #navAccountLink{color: #D8DFEA !important;}
div.loggedout_menubar_container{background-color: green !important;}
.fbFeedTicker .fbFeedTickerStory .uiStreamPassive, .fbFeedTicker .fbFeedTickerStory .tickerAttachmentBodyText, .fbFeedTicker .tickerStoryClickable a, .fbFeedTicker .tickerStoryClickable .passiveName, .fbFeedTicker .tickerStoryClickable .token {color: #333333 !important;}
.UIActionLinks_bottom a, .UIActionLinks_bottom button.as_link, .UIActionLinks_bottom .uiLinkButton input, .UIActionLinks_bottom .uiLinkButton input:hover{color: green !important;}
