.fbTimelineScrubber .selected a, .fbTimelineScrubber .selected ul a{border-left-color: green !important;}
div.uiAttachmentTitle{color: green !important;}
.fbTabGridItem .title{color: green !important;}
.utn .UFIRow{background-color: #CCFFCC !important;}
.uiSideNavCount{background-color: #CCFFCC !important;}
._kv:hover, ._kv{background-color: green !important; border-color:darkgreen !important}
div.uiTypeaheadView .selected{background-color: green !important; border-color: darkgreen !important;}
.uiSideNavCount{color:green !important;}
#pageNav .navLink:hover, #pageNav .navLink:focus, #pageNav .navLink:active{background-color: #99cc66 !important;}
#pageLogo a:hover, #pageLogo a:focus, #pageLogo a:active{background-color: #99cc66 !important;}
#pageNav .navLink:after{background: none repeat scroll 0 0 #ccffcc !important;}
}
