#videoTagContainer{
    display:none !important;
}
.socialLinks{
    margin-right:80px !important;
}
.toggleDetailExpand{
    margin-right:80px !important;
}

/* マイリストボタン等 常時表示 */
.videoMenuToggle{
    display:none !important;
}
#videoMenuWrapper{
    height:90px !important;
}
#videoMenuTopList li{
    display:block !important;
}
#videoHeader.noAward .menuAward {
    display:none !important;
}
#videoHeader.noAward .videoMenuList.userChannel {
    display:none !important;
}

/* ニュース非表示 */
#textMarquee{
    display:none !important;
}
#playerContainerWrapper #playerAlignmentArea.size_normal{
    height:557px !important;
}
#playerAlignmentArea.size_normal #playerContainer{
    height:557px !important;
}
#playerAlignmentArea.size_normal #playerTabWrapper{
    height:557px !important;
}
#playerContainerWrapper #playerAlignmentArea.size_medium{
    height:461px !important;
}
#playerAlignmentArea.size_medium #playerContainer{
    height:461px !important;
}
#playerAlignmentArea.size_medium #playerTabWrapper{
    height:461px !important;
}    
body.full_with_browser #playerAlignmentArea.size_normal #playerContainer{
    height:100% !important;
}
body.full_with_browser #playerContainerWrapper #playerAlignmentArea{
    height:100% !important;
}

/* コメントパネル非表示 */
#playerTabWrapper{
    display:none !important;
}
body.full_with_browser #playerAlignmentArea{
    margin-left:0px !important;
    width:100% !important;
}
#playerAlignmentArea.size_normal{
    width:898px !important;
}
#playerAlignmentArea.size_medium{
    width:672px !important;
}

