style.css:
#nav-bar {
      background-image: none !important;
      
    }

    .tabbrowser-tab:not(:-moz-lwtheme):not([selected="true"]), .tabs-newtab-button:not(:-moz-lwtheme) {
      background-image: none !important;
      border: 0 solid transparent !important;
      padding: 0 9px !important;
    }
    
    .tabbrowser-tab:not(:-moz-lwtheme):not([selected="true"]):hover, .tabs-newtab-button:not(:-moz-lwtheme):hover {
      background-image: none !important;
      background-color: #222 !important;
      border: 1px solid #0088cc !important;
      border-bottom: 0px transparent !important;
    }

    .tabbrowser-tab[selected="true"] {
      background-image: -moz-linear-gradient(hsla(0,0%,10%,1), hsla(0,0%,16.5%,1) 50%) !important;
      border: 1px solid #0088cc !important;
      border-bottom: 0px transparent !important;
    }

    .tabbrowser-tab[selected="true"]:hover {
      background-image: -moz-linear-gradient(hsla(0,0%,1%,1), hsla(0,0%,16.5%,1) 50%) !important;
      
    }
