/* 
 * Name		:	Firefox: Hidden password remove buttons.css 
 * Author	:	iwo
 * Created	:	Nov 31, 2012
 * Description	:	NOT display the Remove password button Never Ever PLS!
 * note		:	Believe it or not, there is almost NO way to undo deleting passwords!
 * Update url		：
 * Usage		:	用 Stylish 扩展导入或者将之粘贴到你的 userChrome.css 文件中。
 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://passwordmgr/content/passwordManager.xul) {

button[id="removeSignon"], 
button[id="removeAllSignons"]/*,
button[id="togglePasswords"]/*uncommente for public used computer as a privacy trick*/
{ display:none !important; }

}
