style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {

#newtab-search-container, #newtab-intro-what, .newtab-side-margin{
  display: none;
}
#newtab-scrollbox:not([page-disabled]) {
    font-family: Segoe UI,Tahoma,Arial,Verdana,sans-serif;
    background: url("http://nyapics.eu/images/24.png") no-repeat fixed right bottom, url(data:image/gif;base64,R0lGODlhCAAIAJEAAMzMzP///////wAAACH5BAEHAAIALAAAAAAIAAgAAAINhG4nudroGJBRsYcxKAA7) repeat fixed, #eee;
}
#newtab-horizontal-margin {
    display: block!important;
}
#newtab-grid{
    max-width: 1050px!important;
    margin: 0 auto;
}
.newtab-cell .newtab-title, .newtab-cell:hover .newtab-title, .newtab-control-pin, .newtab-control-pin[pinned], .newtab-control-block, .newtab-control-block:active{
    transition: 0.3s;
}
.newtab-cell {
    background-color: rgba(255, 255, 255, 0.9);
    border: 10px solid rgba(255, 255, 255, 0.9);
    box-shadow: 0 0 2px 2px #ccc, 0 0 2px 2px #ccc inset;
    border-radius: 50%;
    width:200px;
    height:200px;
}
.newtab-cell:empty{
    opacity: 0.5;
    outline: none;
}
.newtab-thumbnail, .newtab-title {
    border-radius: 50%;
    background-position: 50% 0;
}
.newtab-site, .newtab-thumbnail{
    box-shadow: 0 0 2px 2px #ccc inset!important;
}
.newtab-site, .newtab-site:hover, .newtab-site[dragged] {
    border-radius: 50%;
}
.newtab-title, .newtab-site[pinned] .newtab-title{
    background: rgba(0, 0, 0, 0.3);
    color: #fff!important;
    font-size: 16px;
    text-align: center;
    padding:90px 0;
    opacity:0;
}
.newtab-cell:hover .newtab-title{
    opacity:1;
}
.newtab-control{
    border-radius: 50%;
}
.newtab-sponsored, .newtab-title {
    bottom: 0px;
}
.newtab-control-pin {
    left: 30px!important;
    top: 30px!important;
}
.newtab-site[pinned] .newtab-control-pin{
    box-shadow: 0 0 2px 2px #eee;
}
.newtab-control-block {
    right: 30px!important;
    top: 30px!important;
}
.newtab-control-block:active{
    box-shadow: 0 0 2px 2px #eee;
}
.newtab-site[pinned] .newtab-title::before {
    background-image: none;
}
}
