style.css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document url("about:newtab") {
	#newtab-scrollbox {
		background-color: window !important;
	}

	#newtab-scrollbox:not([page-disabled]) {
		background-color: buttonface !important;
		background-image: url("chrome://browser/skin/newtab/noise.png"), linear-gradient(threedshadow, window) !important;
		background-attachment: fixed !important;
	}

	.newtab-cell {
		background-color: threedshadow !important;
		border: 1px solid infobackground !important;
	}

	.newtab-cell:empty {
		border: 1px dashed infobackground !important;
	}

	.newtab-title {
		background-color: infobackground !important;
		color: infotext !important;
	}
}
