style.css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')
{

body.logged_in
{
background: url('/*[[theimage]]*/') fixed !important;
background-size: 100% Auto !important;
}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]
{
opacity:0.00 !important;
}

body, a, .follower .controls .chrome, .help_search input, #help_footer h3, .pre_as_input, input.text_field, 

textarea, .publish_on_day, .publish_on_time, .last_updated
{
color: /*[[thecolor]]*/ !important;
}

#posts .post blockquote, .notification blockquote, a
{
border-color: /*[[thecolor]]*/ !important;
}

#new_post, #help_footer
{
background-color:rgba(255,255,255, /*[[baropacity]]*/)!important;
}

#tabs 
{
background: none no-repeat scroll left top rgba(234, 234, 234, .25) !important;
border-bottom: 1px solid rgba(202, 201, 202, .25) !important;
}

#tabs .tab.selected 
{
background: none repeat scroll center top rgba(255, 255, 255, .25) !important;
}

.help_search 
{
margin: -25px 0px 0px -40px !important;
background: none repeat scroll 0% 0% rgba(255, 255, 255, .55) !important;
}

.white_pane 
{
background-color:rgba(255,255,255,.25)!important;
border: 3px solid rgba(238, 242, 249, .25) !important;
}

.help_search_big_yellow_notice 
{
background: none repeat scroll 0% 0% rgba(255, 235, 175, .45) !important;
}

#help_footer 
{
background: none repeat scroll 0% 0% rgba(250, 250, 250, .15) !important;
}

#page_nav, div#main_column
{
margin: -25px 0px 0px 0px !important;
}

div.l-content #tabs 
{
margin: -25px 0px 30px -0px !important;
}

#posts .post_footer_links 
{
white-space: normal !important;
overflow: visible !important;
}

.source_url 
{
white-space: normal !important;
max-width: 290px !important;
}

#tabs 
{
border-bottom: 1px solid rgba(202, 201, 202, .25) !important;
}

#tabs .tab 
{
border-left: none !important;
border-right: none !important;
}

#service_providers 
{
background-color: rgba(243, 243, 243, .25) !important;
}

#service_providers .provider_tab
{
background-color: rgba(239, 239, 239, .25) !important;
}

#staff_picks #cards .card 
{
background-color: rgba(255, 255, 255, .25) !important;
}

.new_post_label, #tags .header DIV A, #staff_picks #cards .card .info .desc, #staff_picks #cards .card .location, #finder_container h2.find_friends_header, #finder_container .find_friends_byline, .crushes_subhead
{
color: /*[[thecolor]]*/ !important;
}

.l-container.l-container--two-column-dashboard .left_column
{
background:rgba(0,0,0,0) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.25) !important;
border-radius:12px !important;
}

.mceContentBody, .photo_form .url_button, .drop_zone .help, .video_option textarea 
{
color:  /*[[thecolor]]*/ !important;
}

#content_top, #content_bottom
{
width:0px!important;
height:0px!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
padding-top:25px!important;
padding-bottom:25px!important;
 -moz-border-radius: 20px !important;
}

LABEL 
{
color:rgba(0,0,0,1.0)!important;
}

#footer
{
display: none !important;
}

.post.link.not_mine:hover,.post.quote.not_mine:hover,.post.audio.not_mine:hover,.post.video.not_mine:hover,.post.regular.not_mine:hover,.post.note.not_mine:hover,.post.photo.not_mine:hover
{
border-top-right-radius: 24px !important;
opacity:1!important;
}

.permalink
{
background:none!important;
width:16px!important;
height:16px!important;
background:rgba(170,179,188, .85)!important;
border-bottom-left-radius:10px !important;
border-top-right-radius:24px !important;
margin-top:6px !important;
margin-right:6px!important;
}

.permalink:hover
{
background:rgba(127,139,152,1) !important;
}

.selected .following,.selected .followers,.selected .settings,.selected .posts,.selected .messages,.selected .drafts,.selected .queue,.selected .likes
{
color:rgba(255,255,255,0.50)!important;
background:rgba(44,71,98, 1.0)!important;
}

.mceContentBody a, .label_field label
{
color: #000000 !important;
}

#tumblelog_select .txt, .dashboard_options_form, .tag_psa, .dashboard_options_form .dashboard_options_form_disclaimer, #post_controls button.close, p.result .title, p
{
color:  /*[[thecolor]]*/ !important;
}

#post_form .popover_post_options .option 
{
color:  /*[[thecolor]]*/ !important;
text-shadow: 0px 1px rgba(255, 255, 255, .15) !important;
}

#posts .post .conversation_lines .chat_line:nth-child(2n)
{
background: none repeat scroll 0% 0% rgba(253, 253, 253, .25) !important;
}

#preview .chrome.ui_disabled, .main_content input[type="text"], .main_content textarea, #post_controls button.txt
{
color:  /*[[thecolor]]*/ !important;
}

.tags
{
white-space: normal !important;
line-height: 15px !important;
}

.additional_options label span 
{
color: #000000;
}

.source_url, .notification, .tag_editor, .notification_follow span.follow, #search_query 
{
color:  /*[[thecolor]]*/ !important;
}

input#search_query.popover_button:hover, div.hide_overflow, div.hide_overflow:hover, #dashboard_controls_open_blog #open_blog_link, #right_column .controls_section li .open_blog .hide_overflow, #right_column .controls_section li .open_blog .hide_overflow:hover
{
color:  /*[[thecolor]]*/ !important;
}

.popover_menu_item .hide_overflow, div.left_column, h1, .help_text, #right_column .controls_section li a 
{
color:  /*[[thecolor]]*/ !important;
}

#right_column .controls_section li a#xfollowers_button.followers
{
color:  /*[[thecolor]]*/ !important;
}

.vertical_tab.selected 
{
text-shadow: 1px 1px 1px rgba(255, 255, 255, .1) !important;
}

#posts .post .post_question
{
background: none repeat scroll 0% 0% rgba(231, 234, 236, .95) !important;
}

#posts .post .post_question_asker, #right_column .controls_section a .count 
{
color:  /*[[thecolor]]*/ !important;
}

#posts .post .post_question 
{
background: none repeat scroll 0% 0% rgba(231, 234, 236, .3) !important;
color:  /*[[thecolor]]*/ !important;
}

.source_url .source_url_gradient 
{
background: none !important;
}

.source_url 
{
overflow: visible !important;
}

#right_column .controls_section .with_subtitle small, #posts .post .post_info, #posts .post .post_controls 
{
color:  /*[[thecolor]]*/ !important;
}

#posts .post .post_controls .reblog_count span 
{
color:  /*[[thecolor]]*/ !important;
}

#post_form .popover_post_options ul li .option .title 
{
color: #444444 !important;
}

#posts li[class*=" post"] 
{
background: rgba(225,225,225,  /*[[postopacity]]*/) !important;
}

#posts .post .arrow 
{
background: none !important;
}

#new_post .arrow
{
opacity: /*[[postopacity]]*/!important;
margin-left:-1px !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"])
{
background:none!important;
width:0px!important;
height:0px!important;
margin-top:0px!important;
border-bottom:solid 11px rgba(0,0,0,0.25)!important;
border-right:11px solid transparent!important;
border-left: 11px solid transparent!important;
}

.selection_nipple:not([class="selection_nipple white"])
{
border-bottom:solid 11px rgba(0,0,0,0.25)!important;
}

#categories .category,#categories .category.active
{
color:rgba(255,255,255,.85)!important;
}

#dashboard_directory #tabs, #main_explore #wire
{
margin-top:-25px !important;
}

#new_post_label_text,#new_post_label_photo,#new_post_label_quote,#new_post_label_link,#new_post_label_chat,#new_post_label_audio,#new_post_label_video
{
font: normal .85em verdana !important;
text-transform:lowercase!important;
} 

li.selected, li.recessed, a.active, div.post_filter_tabs a 
{
background: none !important;
}

#search_query:hover, .right_column_section li a:hover,#right_column UL:first-child LI:first-child A:hover,#right_column UL:nth-child(3) LI:first-child A:hover
{
background:rgba(255,255,255,.25)!important;
color:white!important;
}

.right_column
{
background: none !important;
}

.pre_as_input, input.text_field, textarea 
{
background:rgba(255,255,255,.45)!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
box-shadow: none !important;
}

.chrome
{
color:  /*[[thecolor]]*/ !important;
}

.chrome.white, .chrome.flat, select#user_language
{
color:  /*[[thecolor]]*/ !important;
background:rgba(255,255,255,.45)!important;
}

.chrome.private_answer_button, .chrome.ask_cancel_button, #preview .chrome
{
color:  /*[[thecolor]]*/ !important;
}

.notes 
{
color:  /*[[thecolor]]*/ !important;
}

.notes .note
{
background:rgba(255,255,255,.25)!important;
border-left: 1px solid   /*[[thecolor]]*/ !important;
border-right: 1px solid   /*[[thecolor]]*/ !important;
text-shadow: 0px 1px 1px rgba(255, 255, 255, .25) !important;
}

.notes .note:first-child 
{
border-top: 1px solid  /*[[thecolor]]*/ !important;
}

.notes .note:not(:first-child) 
{
border-top: 1px solid  /*[[thecolor]]*/ !important;
}

.notes .note:last-child 
{
border-bottom: 3px solid  /*[[thecolor]]*/ !important;
}

div.settings_container 
{
padding: 10px 0px !important;
}

.post.photo_post .photo,.post.photo_post .video_thumbnail
{
-moz-box-shadow: 1px 1px 3px #000000 !important;
border-radius:12px!important;
}

.content.is_media,.mask.top,.mask.bottom
{
background:none!important;
}

div#right_column,div#right_column:after
{
background: none !important;
}

#finder_container .state0_icon 
{
height: 127px !important;
width: 422px !important;
margin-left: 290px !important;
background-image: url("http://img.photobucket.com/albums/v420/KaKaVegeGurl/StickSPN2_zpsddb8a54e.png") !important;
}

.font_mono-1, .message, #to_container, #fan_mail, #message 
{
font:14px Courier,monospace !important;
}

#fan_mail #message.font_mono-1
{
font-size:14px !important;
}

.font_cursive-1, .message, #to_container, #fan_mail, #message  
{
font:14px daniel,cursive !important;
}

.font_sans-1, .message, #to_container, #fan_mail, #message  
{
font:14px 'Helvetica Neue','HelveticaNeue',Helvetica,Arial,sans-serif !important;
}

.ace-tm .ace_line .ace_string 
{
color: rgb(81, 230, 8) !important;
}

#ace_source_editor .ace_line .ace_tag, #ace_source_editor .ace_line .ace_meta, #ace_source_editor .ace_line .ace_entity, #ace_source_editor .ace_line .ace_other
{
color: rgb(255, 255, 255) !important;
}

#ace_source_editor .ace_line 
{
color: rgb(255, 255, 255) !important;
}
}

@-moz-document url-prefix('http://www.tumblr.com/inbox'),url-prefix('https://www.tumblr.com/inbox')
{
div.l-header .selection_nipple 
{
top: 13px !important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/tagged")
{
div.l-header .selection_nipple 
{
top: 0px !important;
}
}
