style.css:
/*

STYLE BY TEEPEE STUDIOS

WWW.TEEPEESTUDIOS.NET

*/

@-moz-document domain("techcrunch.com") {
/* Hiding elements */
#adsDiv0, #adDiv, #right-rail-ad, #header-search, .bottom-container, .middle-container, .top-featured-posts, #tc_banner_nav_widget-9, .module-got-a-tip, #grv_best_stories, #tc_banner_nav_widget-3, .right-container, .module-sponsored-ads, .module-footer, .post-category-name, #powered_by_livefyre_new, .meta-info {
display: none !important;
}
}

@-moz-document domain("techcrunch.com") {
/* Menu */
.module-header .site-logo-container {
background: none !important;
background-color: #fdfcfc !important;
}
#fixed-logo {
display: block !important;
margin-top: 7px;
}
.center-container {
background-color: #fdfcfc !important;
}
.top-container {
background-color: #fdfcfc !important;
padding-bottom: 20px;
}
.main-nav {
background-color: #fdfcfc !important;
padding: 10px;
text-align: center !important;
}
.main-nav .menu-item a {
color: #444;
}
.main-nav .current-menu-item a, .main-nav .menu-item > a:hover {
color: #000;
}
.main-nav .sub-menu {
background: #fdfcfc !important;
}
.main-nav .sub-menu a {
margin-top: 5px;
}
.top-container {
-webkit-box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 2px -1px;
-moz-box-shadow: rgba(0,0,0,0.3) 0px 2px 2px -1px;
box-shadow: rgba(0, 0, 0, 0.3) 0px 2px 2px -1px;
}
.column-container {
margin-top: 40px;
}

/* Sidebar */
.module-popular-posts .category-container {
border-bottom: 0px;
}
.global-module-header-default span {
background: none;
color: #000;
}
.left-container {
width: 900px;
}
h1 {
padding-bottom: 10px;
}
.page-current {
font-size: 50px;
padding-bottom: 5px;
}
}

@-moz-document url("http://techcrunch.com/") {
/* Homepage Current Page Link Styles */
.main-nav > li:first-child a {
color: #000 !important;
}
.lazy-share-widget {
display: none;
}
}
