style.css:
@namespace xul url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

@namespace url("http://www.w3.org/1999/xhtml");


@-moz-document url-prefix("about:neterror") {
  #errorPageContainer {
    background-image: url("chrome://global/skin/icons/warning-large.png"),
                      -moz-linear-gradient(left, rgba(255, 255, 255, .8), rgba(255, 255, 255, .8)),
                      url("http://www.anony.ws/i/2013/01/22/E1haB.png") !important;
  }
  #errorLongDesc li:last-child:after {
    content: "Impress McKayla and Obama.";
    display: list-item;
    margin-top: .5em;
  }
  #resurrect { background-color: transparent !important; }
}

@-moz-document url-prefix("about:certerror") {
  #errorPageContainer {
    background-image: url("chrome://global/skin/icons/sslWarning.png"),
                      -moz-linear-gradient(left, rgba(255, 255, 255, .8), rgba(255, 255, 255, .8)),
                      url("http://www.anony.ws/i/2013/01/22/E1haB.png") !important;
  }
  #getMeOutOfHereButton {
    text-transform: lowercase;
  }
  #getMeOutOfHereButton:before {
    content: "Impress McKayla and Obama & ";
    text-transform: none;
  }
  #expertContent + div > p:nth-child(2):after {
    content: " McKayla and Obama will not be impressed!";
    font-weight: bold;
  }
}

@-moz-document url-prefix("about:neterror"),
               url-prefix("about:certerror") {
  #errorPageContainer {
    background-position: 0 0, 0 0, right bottom !important;
    background-repeat: no-repeat, repeat, no-repeat !important;
    background-attachment: scroll, scroll, fixed !important;
    color: #000;
  }
  #errorPageContainer:after {
    content: "";
    background-color: transparent;
    background-image: url("http://www.anony.ws/i/2013/01/22/E1haB.png");
    background-position: right bottom;
    background-repeat: no-repeat;
    background-attachment: fixed;
    position: fixed;
    top: 0; right: 0; bottom: 0; left: 0;
    pointer-events: none;
    z-index: -1;
  }
  #errorTitleText:after {
    content: ". McKayla and Obama are not impressed!";
  }
}*{}
