style.css:
@-moz-document domain('forum.html.it') {

   #stripbar, #ads, 
   h1 ~ iframe,
   #content > script + div,
   #eyeDiv,
   br + table + table + div,
   #content > table:first-child td + td object,
   #content > table:first-child td + td embed,
   #content > table:first-child td + td iframe
  
   
   { display: none !important;
     visibility: hidden !important; 
     height: 0 !important; } 
}
