style.css:
@-moz-document domain("pheed.com") {
#suggest-wi-box { display:none; }
 
.pheed .name-date { margin: 0 11px 0 15px; }
 
.pheed .name-date,.pheed .name-date .date a,.pheed .icons,.pheed .pdbk .text ,.pheed .pdbk .icon { float: right; }
 
.pheed .content { direction: rtl; }
 
	.pheed .content .body {
		font-size: 1.5em ;
		line-height: 2.7em; }

			.pheed .content .body a {color:#79a3e1;}
 
.pheed .gray-info { margin: 0 4px 0 10px; }
 
.pheed .pdbk .text {
	font-size: 1.2em;
	line-height: 1.9em;
	color: #777; }
 
.pheed .pdbk .icon {
	margin-left: 8px ;
	margin-right: 0; }
 
.pheed .pdbk .date {
	font-size: 1.2em;
	position: absolute;
	left: 7px;
	right: auto;
	color: #808080;
	float: left; }

#new-pheed-text {
        direction: rtl;}
}
