style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asterisk.org"),
domain("digium.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, .homepage-banners, #splitter-sidebar, .main-menu-tab, .callout-wide, #sidebar-first-inner, .cat-callout-block, .digium-header, .fat-tooltip, 
.footer-inner, .customer-logos, .digium-sidebar, .content ul li

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}


.homepage-banners

{
border-top: 20px solid rgba(0,0,0, .0) !important;
}

#section-content

{
    border: 3px solid #000000 !important;
}

}
