style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("user.qzone.qq.com") {

/*background*/
body{
    background: url("/*[[bgimg]]*/") no-repeat center fixed !important;
    background-size: 100% auto !important;}
#layBackground,.lay_lace_m,#pageContent{
    background: transparent !important;}
#pageContent{
    width: 100% !important;}
.lay_copyright{
    display: none !important;}
a:link,a:visited{
    color: #105009 !important;}
a:hover{
    color: #105009 !important;
    background-color: #60BF00 !important;
    text-shadow: #F6F6F6 0px 1px 0px !important;}
/*top*/
#QZ_Toolbar_Container{
    background: transparent !important;}
#aNavBarLogo,#liAppCenter,#liMallEntry,.search.animation.fl,
#divPayVip,.msg.sep_line.fr{
    display: none !important;}
#QZ_Toolbar_Container li,#divUserMenu{
    background-color: rgba(255,255,255,0.4) !important;}
.a_txt.now_on{
    background-color: rgba(255,255,255,0.2) !important;}
.userinfo.fr,#divUserManage{
    background-color: rgba(255,255,255,0.3) !important;}
/*left*/
#QM_OwnerInfo_NickName,#QM_OwnerInfo_VipBar,#QM_OwnerInfo_GetVip,#QM_OwnerInfo_MyFriend{
    display: none !important;}
#QM_My_App_Container,#QM_VIP_Recommend_App_Container,#QM_My_App_Container,
#QM_VIP_Recommend_App_Container,#gdtApprec,#gdtpaipai,#QM_Container_11{
    display: none !important;}
#colMenu{
    background-color: rgba(255,255,255,0.2) !important;
    width: 11% !important;
    position: absolute !important;
    left: 0px !important;}
.collet_box.fn_profile.clearfix{
    width: 100% !important;}
.ui_avatar_box{
    position: absolute !important;
    left: 24% !important;
    top: 26% !important;}
/*middle*/
#ofp_tips,.f_ang_t.bor2.qz_comment_arrow,.f_like._likeInfo{
    display: none !important;}
.col_main_feed{
    background-color: rgba(255,255,255,0) !important;
    width: 69.1% !important;
    position: absolute !important;
    left: 11% !important;}
#QM_Mood_Poster_Container{
    background: transparent !important;}
.f_ct.f_ct_2.bor3.bg3{
    background: transparent !important;}
.textinput.textarea.c_tx2{
    background-color: rgba(255,255,255,0.4) !important;
    font-size: 18px !important;
    border-color: #0099FF !important;}
.btn.gb_bt{
    background-color: rgba(255,255,255,0.4) !important;
    color: black !important;}
#IC_FEEDCONTROL_FN,.f_single.imgBlock.bor2.list_hover,.f_ct.f_ct_2.bor3.bg3{
    border: none !important;}
#ICC_FILTER_All,#ICC_FILTER_Photo,#ICC_FILTER_Blog,#ICC_FILTER_Renzheng,.drop_list.bor2.bg{
    background-color: rgba(255,255,255,0.5) !important;}
#ICFeedsUpdateTipBTN{
    background-color: rgba(255,255,255,0.2) !important;
    font-size: 16px !important;}
#ICFeedsUpdateTipBTN a{
    color: #333333 !important;}
.nickname.q_namecard.c_tx.ui_mr5{
    font-size: 18px !important;
    font-weight: bold !important;}
.q_namecard.c_tx,.c_tx.ui_mr5{
    font-size: 17px !important;}
.txt_box,.f_info,.comments_content{
    background-color: rgba(255,255,255,0.3) !important;
    font-size: 17px !important;
    color: #010101 !important;}
.textinput.textinput_default.bor2{
    background-color: rgba(255,255,255,0.2) !important;
    border-color: #0099FF !important;}
.qz_emoji,.f_user_vip.qz_ichotclick,.qz_ichotclick.ui_mr5,.appfeed_list,.mr8,.info_user_hat.f_user_hat{
    display: none !important;}
.vote_item.bg2.bg3_hover,.vote_btnbar.bg2,.vote_illustrate.bg2.c_tx3{
    background-color: rgba(255,255,255,0.3) !important;}
.qz_hot_keyword.bg2.c_tx{
    background-color: rgba(255,255,255,0.3) !important;
    color: red !important;}
#ICC_SUBTAB_Host,#ICC_SUBTAB_AboutHost{
    background-color: rgba(255,255,255,0.4) !important;}
.ui_mr10,.op.c_tx3,.c_tx3{
    color: black !important;}
/*right*/
#checkin_btn_container,#QM_Container_31,.collet_box.fn_gdtads,.collet_box.fn_qqlogin,
.collet_box.fn_hotTopic,.collet_box.fn_mayKnow.fn_guanxiquan{
    display: none !important;}
#QM_Group_Container_3{
    background-color: rgba(255,255,255,0.2) !important;
    width: 15% !important;
    position: absolute !important;
    right: 0px !important;}
.recent_visit_stat{
    width: 75% !important;}
#visitor_detail{
    position: absolute !important;
    top: -450% !important;
    right: 0px !important;}
.fn_accessLog_tips.bg2.bor2,#today_refuse_visitor{
    display: none !important;}
.name.textoverflow.q_namecard{
    font-size: 13px !important;}
/*other*/

/*weibo*/
#app_right_container{
    width: 89% !important;
    position: absolute !important;
    right: 0px !important;}
/*specialcare*/
#QM_SpecialCare_Container{
    position: absolute !important;
    right: 0px !important;
    width: 15% !important;}
#ICC_SPECIAL_All,#ICC_SPECIAL_Single{
    background-color: rgba(255,255,255,0.4) !important;}
#divCarePromote,.btn_special_add.bgr2{
    background-color: rgba(255,255,255,0.3) !important;}
/*aboutme*/
.f_ct.bg2.bor2.f_ct_friendsvisit{
    background-color: rgba(255,255,255,0.3) !important;}
/*sb*/
.qz_vip_icon_s.qz_vip_icon_s_1,
.qz_vip_icon_s.qz_vip_icon_s_2,
.qz_vip_icon_s.qz_vip_icon_s_3,
.qz_vip_icon_s.qz_vip_icon_s_4,
.qz_vip_icon_s.qz_vip_icon_s_5,
.qz_vip_icon_s.qz_vip_icon_s_6,
.qz_vip_icon_s.qz_vip_icon_s_7,
#user_deco,.qzone_cards_app{
    display: none !important;}
.textarea.c_tx2{
    background: transparent !important;
    background-color: rgba(255,255,255,0.3) !important;}
.banner.fl,.tablist.setup.fr,.userinfo.fr{
	opacity: 0 !important;
	-moz-transition-property: opacity !important;
	-moz-transition-duration: 0.4s !important;
	-moz-transition-timing-function: ease-out !important;
}
.banner.fl:hover,.tablist.setup.fr:hover,.userinfo.fr:hover{
	opacity: 1 !important;
}

}
