style.css:
@-moz-document url-prefix("http://cruise."), url-prefix("http://go."), url-prefix("http://cruise/"), url-prefix("http://go/") {

#pipelines .dashboard_build_cause_like_microcontent_popups .enhanced_dropdown, #stages .dashboard_build_cause_like_microcontent_popups.fbh_microcontent_popup .enhanced_dropdown {opacity: 0.85; width: 800px !important}
#pipelines .pipeline_bundle div.pipeline { float: none !important; }
#pipelines .pipeline_bundle .pipeline {float: none !important; width: 100% !important; box-shadow: none !important; border-radius: 0px !important; border: none !important; min-height:0px !important; margin: 0px !important; padding:0px !important}
#pipelines .divider{display: none}
#pipelines .pipeline_header {border-bottom: none !important; margin-bottom: 0px !important; padding-bottom:0px !important; width:200px !important;}
#pipelines .pipeline_operations{border-top: none !important }
#pipelines .label{float:left !important; margin: 0px !important}
#pipelines .pipeline_instance_details{float:left !important; }
#pipelines .schedule_time{float:left !important; width: 230px}
#pipelines h3.entity_title{position:relative;top:12px;left:5px;font-size:12px !important; padding: 0px !important; background-image: none !important;}
#pipelines h2.entity_title{background-image: none !important}
#pipelines .pipeline_instance{float:left !important}
#pipelines .pipeline_bundle div.pipeline .pipeline_instance{padding:0px !important}
#pipelines .stages{position:relative;left:20px;float:left !important; clear: none !important; width: 450px; overflow-y: hidden; overflow-x: hidden}
#pipelines .stage{ height: 5px !important;}
#pipelines .pipeline_bundle .pipeline .status .pipeline_instance_details {margin-top: 0px !important; float: left !important}
#pipelines .pipeline_bundle .pipeline .previously_wrapper {margin: 0px !important}
#pipelines .latest_stage {float:left !important; width:200px;position:relative;left:0px} 
#pipelines .pipeline_bundle .pipeline_operations {clear: none !important; margin-top:0px !important; padding-top:0px !important;}
#pipelines .stage_bar_wrapper  .last_run_stage{top: -10px !important; }
#pipelines .pipeline_group h2.entity_title{padding-left:0px !important}
#pipelines .pipeline .stages .stage_bar {margin-top: 0px !important; height: 10px !important }
#pipelines #body_content{left:150px;top:20px;position:absolute;width:1200px}
#pipelines .tabs{position:fixed;top:50px;left:0px;right:auto; z-index:-100}
#pipelines .tabs li {float:none !important}
#pipelines .compare_pipeline a {background:none !important}
#pipelines .pipeline_bundle .pipeline_instance .changes_button_wrapper a, .non_passing_tests .changes_button_wrapper button span {background:none !important}
#pipelines .pipeline_instance{margin-left: 30px !important; width:700px !important}
#pipelines .pipeline_instance:nth-child(n+4){margin-left: 230px !important; width:700px !important}
#pipelines #application_logo{display: none}
#pipelines #header {width: 90%; background: none !important}
#pipelines #header .header {padding: 5px !important; background: none !important}
#pipelines #footer {position: absolute; top:0px; margin: 0px !important;padding: 10px 0 !important}
#pipelines div > .pipeline:nth-of-type(4n + 3) {background: #EEE !important}
#pipelines button {position:relative;top:5px;left:40px}
#pipelines #pipelines_selector_pipelines{max-height:600px !important}
#pipelines #pipelines_selector{max-height:900px !important;left: 250px !important; top: 10px !important;}
#pipelines .pause_description{position: relative; left:5px; top:-5px}
#pipelines .help{display:none}
#pipelines .content_wrapper_inner, #environments .content_wrapper_inner{
    border-color:black;
    border-style:solid;
    border-width:1px;
}

#pipelines div.pipeline_instance_details > .stages > .stage:nth-child(2) > .stage_bar_wrapper > .stage_bar {border-top-left-radius:7px !important;border-bottom-left-radius: 7px}
#pipelines div.pipeline_instance_details > .stages > .stage:last-child > .stage_bar_wrapper > .stage_bar  {border-top-right-radius: 7px !important;border-bottom-right-radius: 7px !important}

#pipelines div.pipeline_instance_details > .stages > .stage:nth-child(2) > .stage_bar_wrapper > .stage_bar {border-top-left-radius:7px !important;border-bottom-left-radius: 7px}
#pipelines div.pipeline_instance_details > .stages > .stage:last-child > .stage_bar_wrapper > .stage_bar  {border-top-right-radius: 7px !important;border-bottom-right-radius: 7px !important}

#stages div.pipeline  .stages  .stage:first-child * {border-top-left-radius:7px !important;border-bottom-left-radius: 7px}
#stages div.pipeline  .stages  .stage:last-child *  {border-top-right-radius: 7px !important;border-bottom-right-radius: 7px !important}

#pipelines .Failing, #pipelines .Building  {

    height: 20px !important;
       
}
}

