style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* colores de fondo */
@-moz-document url-prefix("http://subcultura.es/foro/webcomics"),
url-prefix("http://subcultura.es/foro/taller"),
url-prefix("http://subcultura.es/foro/ayuda_y_sugerencias"),
url-prefix("http://subcultura.es/foro/betatesting") {
   body {
      color: #000000 !important;
      background-color: #EEF2FF !important;
   }
   .fc-gris,
   input[type="submit"] {
      color: #000000 !important;
   }
   #lista_posts>li,
   ul.paginacion,
   input[type="submit"] {
      background-color: #D6DAF0 !important;
   }
   #lista_posts>li {
      border-right: 1px !important;
      border-bottom: 1px !important;
      border-style: solid !important;
      border-color: #B7C5D9 !important;
   }
   h4>a {
      color: #0F0C5D !important;
      font-weight: bold !important;
   }
}
@-moz-document url-prefix("http://subcultura.es/foro/off-topic"),
url-prefix("http://subcultura.es/foro/quedadas"),
url("http://subcultura.es/foro/"),
url("http://subcultura.es/foro") {
   body {
      color: #800000 !important;
      background-color: #FFFFEE !important;
   }
   .fc-gris,
   input[type="submit"] {
      color: #800000 !important;
   }
   #lista_posts>li,
   ul.paginacion,
   input[type="submit"] {
      background-color: #F0E0D6 !important;
   }
   #lista_posts>li {
      border-right: 1px !important;
      border-bottom: 1px !important;
      border-style: solid !important;
      border-color: #D9BFB7 !important;
   }
   h4>a {
      color: #CC1105 !important;
      font-weight: bold !important;
   }
}

/* general */

@-moz-document url("http://subcultura.es/foro/"),
url("http://subcultura.es/foro") {   
   #foro>.span-18 {
      float: none !important;
      margin: auto !important;
      display: table !important;
   }
}
@-moz-document url-prefix("http://subcultura.es/foro/webcomics"),
url-prefix("http://subcultura.es/foro/taller"),
url-prefix("http://subcultura.es/foro/ayuda_y_sugerencias"),
url-prefix("http://subcultura.es/foro/betatesting"),
url-prefix("http://subcultura.es/foro/off-topic"),
url-prefix("http://subcultura.es/foro/quedadas") {   
   #foro>.span-18 {
      width: 80% !important;
   }
}

@-moz-document url-prefix("http://subcultura.es/foro") {
   body {
      font-size: 10pt !important;
   }
   #foro {
      margin: 0 !important;
      width: 100% !important;
   }
   #foro>* {
      margin-left: 4px !important;
   }
   
   div.mensaje a:not(.usuario),
   ul.paginacion a {
      color: #34345C !important;
      text-decoration: underline !important;
   }
   #lista_subforos h3 {
      padding: 6px !important;
      background: #880000 !important;
   }
   #lista_subforos h3>a {
      color: #FFFFFF !important;
      font-weight: bold !important;
   }
   #lista_subforos .span-10 {
      border: 1px solid #880000 !important;
      background: #FFFFFF !important;
   }
   #lista_subforos .span-10>p {
      padding: 6px !important;
   }
   #lista_subforos .span-1 {
      display: none !important;
   }
   
   #footer {
      display: none !important;
   }
   #foro>div.last>div.columna-i {
      display: none !important;
   }
   
   /* Portada subforos */
   #lista_subforos>li {
      border: 0px !important;
   }
   #lista_subforos>li>div:first-child {
      color: #880000 !important;
   }
   #lista_subforos div.last {
      background-color: #EEFFFF !important;
      border: 1px #000066 solid !important;
      padding: 3px 6px 0 6px !important;
   }
   #lista_subforos div.last a:not(.usuario) {
      color: #0000EE !important;
   }
   #lista_subforos div.last li.fc-gris {
      color: #000066 !important;
   }
   div.icono_hilo {
      display: none;
   }

/*[[mascota]]*/
   
   /* Titulo */
   #titulo_pagina {
       display: table !important;
       margin: 0 auto 0 !important;
       border: 0 !important;
       color: #800000 !important;
       font-family: Tahoma,sans-serif !important;
       font-size: 28px !important;
       font-weight: bold !important;
       letter-spacing: -2px !important;
    }
    #titulo_pagina>a {
       color: #800000 !important;
    }
    h2.h2_verde {
       display: none !important;
    }
   
   /* Nombre e info de usuario */
   div[class="informacion fc-gris"] {
      color: #117743 !important;
      font-weight: normal !important;
   }
   div[class="informacion fc-gris"] * {
      color: #117743 !important;
      font-weight: normal !important;
   }
   div[class="informacion fc-gris"]>span,
   div.opciones_post {
      color: #000000 !important;
   }
   div.opciones_post {
      margin-left: 8px !important;
   }
   div.opciones_post>a {
      color: #34345C !important;
   }
   a.usuario {
      color: #117743 !important;
      font-weight: bold !important;
      text-decoration: none !important;
   }
   
   /* Mensajes */
   #lista_posts>li {
      margin: 2px 0 4px 0 !important;
      padding: 2px !important;
      display: table !important;
   }
   #lista_posts>li:first-child {
      border: 0 !important;
      background: none !important;
   }
   
   /* Firmas */
   div.foro_firma {
      display: none !important;
   }
   
   /* Paginacion */
   ul.paginacion {
      display: table !important;
      margin: 0 !important;
      text-align: left !important;
   }
   ul.paginacion>li,
   ul.paginacion>li>a:hover {
      background: none !important;
   }
   ul.paginacion>li.current {
      color: #000000 !important;
   }
   
   /* Quotes */
   div.bbcode_quote {
      margin: 0 !important;
   }
   div.bbcode_quote * {
      padding: 0 !important;
      background: none !important;
      border: 0 !important;
   }
   div.bbcode_quote_head {
      color: #DD0000 !important;
      text-decoration: underline !important;
      text-shadow: none !important;
   }
   div.bbcode_quote_body {
      color: #789922 !important;
   }
   div.bbcode_quote div.bbcode_quote {
      margin-left: 16px !important;
   }
   span.boton_mostrar_quotes {
      margin: 0 0 0 16px !important;
      display: inline !important;
      color: #34345C !important;
      text-decoration: underline !important;
      font-style: normal !important;
   }
   
   /* Responder */
   #formulario_respuesta div.last {
      display: none !important;
   }
   
   /* Resaltar nicks admins */
   a[href^="http://subcultura.es/user/Patroklo"].usuario,
   a[href^="http://subcultura.es/user/Neverwolf"].usuario {
      color: #FF0000 !important;
   }
   /* Resaltar nicks mods */
   a[href^="http://subcultura.es/user/pziko"].usuario,
   a[href^="http://subcultura.es/user/Marja"].usuario,
   a[href^="http://subcultura.es/user/valerian32"].usuario {
      color: #800080 !important;
   }
}

