style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pathofexile.com") {

#passiveControlsForm textarea, #passiveControlsForm input.textInput, #passiveControlsForm select {
    width: 100% !important;
    margin-right: 15px;
}

#skillTreeInfo {
    display:none !important;
    }

.clear {
    clear:none !important;
    }

#passiveControlsForm {
    width: 98% !important;
    }

#passiveControlsForm .skillTreeInfo, 
#passiveControlsForm .highlightSimilarNodesContainer, #passiveControlsForm .highlightShortestPathsContainer, #passiveControlsForm .muteContainer {
    padding: 20px 2px 2px 2px !important;
    }


.button1.important, .important.button2, .pagination a.important, .pagination .important.current, .pagination .important.currentPage, .important.editor-control {
    font-size: 100% !important;
    margin: 15px 5px 3px 0 !important;
}



}
