style.css:
@-moz-document url-prefix("https://www.tumblr.com/") {
body {
   background: url('http://i39.tinypic.com/i50myh.png') bottom left no-repeat fixed /*[[mycolor]]*/ !important;
 }

.l-content {
background:url('http://i57.tinypic.com/2ql7o0l.png') top no-repeat!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0.2) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;
}

.mceContentBody {background: white !important}


.controls_section li .posts:before {
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
}

.post_permalink {
background: transparent !important;
}

.post_permalink:hover {
background: url('http://i39.tinypic.com/2v2bfrl.png') /*[[mycolor]]*/ !important; 
width: 23px !important;
height: 23px !important;
display: inline !important;
}

.selection_nipple.black, .selection_nipple:not([class="selection_nipple black"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #000000 !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}
 
.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(0,0,0,0.2) !important;}

.link_button:active {
border-color: 1px transparent !important;
background-color: #ddd !important;
}
    
.link_button .link_title {
font-size: inherit !important;
line-height: 20px !important;
}



#right_column .controls_section li a,
#right_column .controls_section li a .count {
color: #e6e6e6 !important; 
}

#right_column {
  background-color: rgba(100, 100, 100, 0) !important;
}

div#right_column:after {
  background: none !important;

}

div.ui_follow:hover, .controls_section li .follow:hover {
background: rgba(0,0,0,.3) !important;
}

div.ui_follow, .controls_section li div.follow {
color: #e6e6e6 !important;
line-height: 8px !important;
font-size: 24px !important;
}

.search_form_row {
background-color: rgba(0,0,0,.3) !important;
}

.controls_section a .count canvas {
canvas-color: rgba(0,0,0,.3) !important;
}

#pagination a {
background-color: rgba(0,0,0,0) !important;
color: #e6e6e6 !important;
}

#dashboard_controls a, #dashboard_controls .control {
color: #e6e6e6 !important;
text-shadow: none;
}

.notifications_page .section.divider {
padding-bottom: 8px;
background: transparent bottom no-repeat;
}

.dashboard_iframe {
background: none !important;
}

.footer_links a {
color: #e6e6e6 !important;

}
}
