}

::selection {
background-color: #046A94 !important;
color: #fff !important;
}

::-moz-selection {
background-color: #046A94 !important;
color: #fff !important;
}

body,
#siteHeader,
#content,
#videoMenuTopList .videoMenuListNicoru .nicorub-balloon,
input, textarea, select, option, label, button {
