}

#content {
overflow: visible !important;
padding-top: 32px !important;
z-index: auto !important;
}

.content-fix #content {
height: 0 !important;
margin: 0 !important;
z-index: 10000 !important;
}

body.full_with_browser:not(.full_and_mini) #content {
padding: 0 !important;
}

#content .videoHeaderOuter {
font-size: 0.7rem !important;
overflow: visible !important;
position: relative !important;
}

#content:before, #content:after, .videoHeaderOuter:before, .videoHeaderOuter:after {
bottom: 0;
content: "";
position: absolute;
left: 0;
right: 0;
top: 160px;
}

#content:before,
#content #videoHeaderTagList .videoHeaderTag:hover,
#videoInfo,
#playerTabWrapper .commentTableContainer .cell:hover,
#reviewLetterCounter {
