}

.videoHeaderOuter:after, #videoInfo {
background-image: linear-gradient(-120deg, rgba(255,255,255,0) 0, rgba(255,255,255,0) 50%, rgba(255,255,255,0.02) 50%, rgba(255,255,255,0.02) 100%);
top: 0;
z-index: 1;
}

/*#content:before {
box-shadow: 0 1px 1px rgba(0,0,0,0.2) inset, 0 1px 4px -1px rgba(0,0,0,0.4) inset;
}*/

.videoHeaderOuter:before {
bottom: -32px;
height: 32px;
top: auto;
z-index: 30;
}

.content-fix .videoHeaderOuter {
opacity: 0 !important;
visibility: hidden !important;
}

/******************************
  siteHeader
 ******************************/

#siteHeader, #siteHeaderInner {
height: 32px !important;
}

#siteHeader, #siteHeaderInner {
background: #252525 !important;
}

.videoHeaderOuter:before,
#playerContainerWrapper,
#playlist .playlistInformation,
body.full_with_browser:not(.full_and_mini) #videoTagContainer,
#videoExplorerExpand a,
#textMarquee .excludeReserveButton:after,
#popupMarquee .closeButton:after {
