}

#siteHeaderInner > ul > li > a {
color: #999 !important;
font-size: 0.7rem !important;
height: 32px !important;
margin: 0 !important;
vertical-align: top !important;
}

#siteHeaderInner > ul > li:not(#siteHeaderNotification):hover a,
#siteHeaderUserNickNameContainer:hover,
#siteHeader #siteHeaderInner #siteHeaderNotification #siteHeaderNicopoPurchase:hover {
