}

#siteHeaderInner > ul > li:hover,
#siteHeader ul li ul,
#siteHeader #siteHeaderInner #siteHeaderNotification #siteHeaderNicopoPurchase:hover,
#siteHeaderUserNickNameContainer:hover,
.searchContainer .searchOption ul li a:hover,
#videoHeaderMenu .searchContainer .searchText input:hover,
