color: #fff !important;
text-decoration: none !important;
transition: opacity ease 0.15s;
}

#siteHeader #siteHeaderInner .siteHeaderGlovalNavigation li ul li.menuServiceList:hover,
#siteHeader #siteHeaderNotification:hover {
background: transparent !important;
}

#siteHeader ul li ul {
border: 0 !important;
padding: 0 !important;
top: 32px !important;
width: 288px !important;
}

#siteHeader #siteHeaderRightMenuContainer {
width: 144px !important;
}

#siteHeader ul li ul li {
background: none !important;
height: 32px !important;
line-height: 32px !important;
width: 144px !important;
}

#siteHeader #siteHeaderRightMenuContainer li {
width: 144px !important;
}

#siteHeader ul li ul li a {
font-size: 0.7rem !important;
color: #fff !important;
padding: 0 16px !important;
margin: 0 !important;
transition: all ease 0.15s;
vertical-align: top !important;
width: 112px !important;
}

#siteHeader ul li ul li a:hover {
background: rgba(0,0,0,0.25) !important;
}

#siteHeader ul li > a span, #siteHeader .siteHeaderNicopo a, #siteHeader #siteHeaderInner #siteHeaderNotification #siteHeaderNicopoPurchase {
border: none !important;
height: 32px !important;
line-height: 32px !important;
padding: 0 5px !important;
vertical-align: top !important;
}

.siteHeaderNicopo {
margin: 0 !important;
}

#siteHeader #siteHeaderInner #siteHeaderNotification #siteHeaderNicopoPurchase {
color: #999 !important;
background: none !important;
margin: 0 !important;
width: auto !important;
}

#siteHeaderNicopoPurchase:after {
font-size: 0.7rem !important;
content: "Pt.購入";
}

#siteHeaderNicopoPurchase:hover span {
display: none !important;
}

#siteHeader ul li a span.siteHeaderScroll,
#siteHeader ul li a span.siteHeaderFix {
height: 18px !important;
line-height: 18px !important;
margin-top: 7px !important;
padding-right: 24px !important;
}

#siteHeader .siteHeaderAva {
background: none !important;
display: block !important;
float: left;
height: 26px !important;
line-height: 26px !important;
margin-top: 3px !important;
padding: 0 !important;
top: 0 !important;
width: 26px !important;
}

#siteHeader .siteHeaderPremium .siteHeaderAva {
height: 24px !important;
line-height: 24px !important;
margin: 4px 4px 0 !important;
width: 24px !important;
}

#siteHeader .siteHeaderAva img {
border-radius: 1px;
height: 20px !important;
margin: 2px 0 !important;
vertical-align: top !important;
width: 20px !important;
}

#siteHeaderUserNickNameContainer {
float: left;
font-size: 0 !important;
}

#siteHeaderUserNickNameContainer:after {
content: "マイページ";
font-size: 0.7rem !important;
}

#siteHeader .menuServiceList,
#siteHeader .siteHeaderHorizon a span {
border: none !important;
}

#siteHeaderNotificationContainer {
font-size: 0.7rem !important;
height: 20px !important;
line-height: 1.5 !important;
margin: 6px !important;
vertical-align: top !important;
}

#siteHeaderNotificationContainer > span {
background: #666 !important;
color: #999 !important;
border: 0 !important;
border-radius: 6px !important;
height: 20px !important;
line-height: 20px !important;
margin: 0 !important;
padding: 0 !important;
vertical-align: top !important;
width: 20px !important;
}

#siteHeaderNotificationContainer > .siteHeaderNotifications {
background: #c02 !important;
color: #fff !important;
}

/******************************
  videoHeader
 ******************************/

#videoHeader {
padding-bottom: 0 !important;
z-index: auto !important;
}

/* search_box */
#videoHeaderMenu {
height: 0 !important;
margin: 0 !important;
position: fixed !important;
top: 0 !important;
width: 1008px !important;
z-index: 10000 !important;
}

.size_normal #videoHeaderMenu {
width: 1234px !important;
}

body.nofix #videoHeaderMenu {
position: absolute !important;
top: -32px !important;
}

#videoHeaderMenu .searchContainer {
margin: 0 auto !important;
}

#videoHeaderMenu .searchContainer .searchText {
background: none transparent !important;
box-shadow: none !important;
height: 32px !important;
padding: 0 !important;
position: absolute !important;
right: 0 !important;
top: 0 !important;
width: 160px !important;
}

.size_normal #videoHeaderMenu .searchContainer .searchText {
width: 240px !important;
}

#videoHeaderMenu .searchContainer .searchText a,
#videoHeaderMenu .searchOption li span {
-webkit-box-sizing: content-box !important;
-moz-box-sizing: content-box !important;
background: url("http://blog-imgs-49.fc2.com/x/u/a/xuai/nicon.png") -225px -32px no-repeat !important;
border: 0 !important;
height: 24px !important;
margin: 0 !important;
opacity: 0.5;
padding: 4px !important;
width: 24px !important;
}

#videoHeaderMenu .searchContainer .searchText .searchTagIcon,
#videoHeaderMenu .searchOption .searchTag span {
background-position: -192px -32px !important;
}

#videoHeaderMenu .searchContainer .searchText a:hover {
background-color: #F26D64 !important;
opacity: 1;
}

#videoHeaderMenu .searchContainer .searchText input {
-webkit-appearance: none !important;
-moz-appearance: none !important;
-webkit-box-sizing: content-box !important;
-moz-box-sizing: content-box !important;
border: 0 !important;
color: #ccc !important;
height: 32px !important;
line-height: 32px !important;
margin: 0 !important;
padding: 0 6px !important;
width: 116px !important;
}

.size_normal #videoHeaderMenu .searchContainer .searchText input {
width: 196px !important;
}

#videoHeaderMenu .searchContainer .searchOption {
background: rgba(0,0,0,0.8) !important;
border: medium none !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2) !important;
left: auto !important;
padding: 4px 0 !important;
right: 24px !important;
top: 42px !important;
}

.size_normal #videoHeaderMenu .searchContainer .searchOption {
right: 104px !important;
}

#videoHeaderMenu .searchContainer .searchOption:after {
border-color: transparent transparent rgba(0,0,0,0.8);
border-style: solid;
border-width: 0 8px 8px 8px;
content: "";
display: block;
height: 0;
left: 32px;
position: absolute;
top: -8px;
width: 0;
}

#videoHeaderMenu .searchContainer .searchOption .searchKeyword a,
#videoHeaderMenu .searchContainer .searchOption .searchTag a {
color: #fff !important;
line-height: 32px !important;
padding: 0 4px !important;
width: 152px !important;
}

.suggestBox {
box-shadow: 0 3px 6px -2px rgba(0,0,0,0.2) !important;
margin: 0 32px 0 0 !important;
min-width: 0 !important;
overflow: hidden !important;
position: relative !important;
top: 0 !important;
}

.suggestBox .suggestBoxInner {
border: 0 !important;
}

#videoHeaderMenu .suggestBox .suggestBoxInner li {
height: 48px !important;
line-height: 48px !important;
padding: 0 !important;
}

#videoHeaderMenu .suggestBox .suggestBoxInner li a {
background: none !important;
display: block !important;
height: 48px !important;
padding: 0 0 0 16px !important;
width: auto !important;
}

#videoHeaderMenu .suggestBox .suggestBoxInner li a:hover {
background: #f4f4f4 !important;
}

/* video_title */
#videoHeaderDetail {
margin: 0 !important;
width: 100% !important;
}

#videoHeaderDetail .videoDetailExpand {
display: block !important;
height: 128px !important;
margin: 0 0 0 84px !important;
overflow: hidden !important;
z-index: 9;
}

#videoHeaderDetail h2 {
font-size: 240% !important;
font-family: 'Open Sans', Verdana, Arial, "小塚ゴシック Pr6N", "小塚ゴシック Pr6N L", sans-serif !important;
font-weight: 300 !important;
height: 128px !important;
line-height: 1 !important;
letter-spacing: -0.05em !important;
max-width: 524px !important;
overflow: hidden !important;
padding: 0 32px !important;
}

.size_normal #videoHeaderDetail h2 {
font-size: 280% !important;
max-width: 750px !important;
}

#videoHeaderDetail .videoHeaderTitle,
.toggleDetailExpand,
#ichibaMainHeader, #ichibaMainHeader a,
#ichibaMain .noItem span, #ichibaMain .noItem a,
.itemname a, .goIchiba a, a.goIchiba, .pia .ichiba,
#videoReview h3,
#videoReview .videoReviewDeny > span,
#reviewLetterCounter,
.videoReviewContrl h4,
#videoReviewComment .reviewTime,
#videoReviewComment h5 a,
#videoReviewComment h5 a:hover,
#ichibaConsoleLoading {
color: /*[[titletext]]*/ !important;
}

.close.videoDetailCloseButton:after,
.open.videoDetailOpenButton:after {
border-color: transparent /*[[titletext]]*/;
}

#videoHeaderDetail .videoHeaderTitle {
cursor: text !important;
}

/* toggleDetailExpand */
.toggleDetailExpand {
border: 4px solid;
border-radius: 50%;
height: 72px;
left: 2px;
margin: 0 auto !important;
opacity: 0.5;
position: absolute !important;
top: 24px;
width: 72px;
z-index: 9;
}

.toggleDetailExpand:hover {
opacity: 1;
}

.videoDetailCloseButton,
.videoDetailOpenButton {
font-size: 0 !important;
height: 72px !important;
position: absolute !important;
width: 72px !important;
}

.close.videoDetailCloseButton:after,
.open.videoDetailOpenButton:after {
border-style: solid;
border-width: 12px 0 12px 18px;
border-radius: 1px;
content: "";
left: 30px;
position: absolute;
top: 24px;
transition: all ease 0.4s;
}

.toggleDetailExpand:hover .close.videoDetailCloseButton:after {
-webkit-transform: rotate(-90deg);
-webkit-transform-origin: 7px 12px;
transform: rotate(-90deg);
transform-origin: 7px 12px;
}

.toggleDetailExpand:hover .open.videoDetailOpenButton:after {
-webkit-transform: rotate(90deg);
-webkit-transform-origin: 6px 12px;
transform: rotate(90deg);
transform-origin: 6px 12px;
}

/* VideoInfo */
.videoDescription,
#videoHeaderTagList .videoHeaderTag:hover .videoHeaderTagLink,
.commentTableContainer .cell:hover > span,
#reviewLetterCounter {
color: /*[[bgtext]]*/ !important;
}

.readMore.videoDetailOpenButton {
display: block !important;
height: 32px !important;
margin: 0 !important;
width: 100% !important;
}

