style.css:
@-moz-document regexp(".*/jira/browse/.*") {
.aui-group{
  display: block;
  position: relative;
}

#viewissuesidebar{
  position: absolute;
  top: 11px;
  right: 0px;
  background: white;
  padding: 10px;
  padding-right: 20px;
  border: 1px solid #ddd;
  width: 300px;
}


#viewissuesidebar .toggle-title{
  margin-left: -10px;
  padding-left: 17px;
} 
  
#viewissuesidebar .item-details dl>dt{
    width: 80px;
}
  
#details-module{
	float: left;  
}
  
#descriptionmodule{
    clear:both;
}

}
