style.css:
/*  home button */
#navigator-toolbox:not([mode="text"]) #home-button > image{ 
    list-style-image: url("data:image/png;base64,") !important;
    -moz-image-region: auto !important; 
    opacity: /*[[mytransparency]]*/;
    margin:-80px -30px -60px -30px!important;
    height:60px!important;
    width:auto!important;
    background:none!important;
    border:0px!important;
    box-shadow:none!important;
}
#navigator-toolbox:not([mode="text"]) #home-button/*[[myhover]]*/ > image{ 
    list-style-image: url("data:image/png;base64,") !important;
}
#navigator-toolbox:not([mode="text"]) #home-button{
    height:25px!important;
    width:30px!important;
    border:1px solid transparent  !important;
}

/* label */
#navigator-toolbox:not([mode="text"]) #home-button > label{
    display:none!important;
}

/*  border */
#navigator-toolbox:not([mode="text"]) #home-button:hover,
#navigator-toolbox:not([mode="text"]) #home-button:active{
    background-image: linear-gradient(rgba(255, 255, 255, 0.6), rgba(255, 255, 255, 0.1))!important;
    border:1px solid  !important;
    border-color: rgba(23,51,78,0.2) !important;
    border-radius:2px !important;
}
#navigator-toolbox:not([mode="text"]) #home-button:active{
    border-color: rgba(23,51,78,0.4) !important;
}
