style.css:
@-moz-document domain("www.x2yun.com") {
body { background:#000;}
div.w960 { display:none;}
div#video,div#operate { display:block;}
div#video { margin-top:200px;}
div#operate-input { border:none;}
div#operate,div#player-head { opacity:0; transition:all 0.5s;}
div#operate:hover,div#player-head:hover { opacity:1; transition:all 0.5s;}
div#bdshare { display:none;}
}
