style.css:
@-moz-document domain("last.fm") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.music-tags {
    grid-gap: 47px;
    -ms-grid-columns: 570px 30px 570px;
    -ms-grid-rows: 570px 30px 570px;
    grid-template-columns: repeat(2,119rem);
    grid-template-rows: repeat(2,604px);
}

/* Fonts 
_____________________________________________________________________________________________________*/

body
{
	font-size: 2.5rem!important;
}

.wiki-truncate-4-lines,
.wiki-truncate-5-lines,
.wiki-truncate-6-lines
{
	line-height: 2.85rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.content-top-has-nav .content-top,
.full-bleed-ad-container,
.lastfm-ad,
.page-content+.full-bleed-ad-container[data-ad-container].open-ad-container-desktop,
.page-content+.full-bleed-ad-container[data-ad-container].open-ad-container-mobile,
.sidebar-ad-container,
.two-column-layout .content-top,

[name*="ad_is_"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.container
{
	width: 98%!important;
}

.col-main {
    width: 83%;
}

}

@-moz-document domain("last.fm") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.music-releases-item-artist+.music-releases-item-aux-text,
.music-releases-item-artist+.music-releases-item-regular-tags,
time
 {
	color: #07beff!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}


}

@-moz-document domain("last.fm") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.column-tracks-item-playlink
{
	background-image: none!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.header--overview,
.column-tracks-item-playlink:before,
.container,
.content-top-has-nav .content-top,
.main-content,
.music-more-tags-tag-background-overlay,
.music-releases-item-playlink:before,
.two-column-layout .content-top,

li
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.header-new-playlink,
.btn-secondary
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.header-new-playlink::before,
.btn-secondary::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.header-new-playlink:hover::before,
.btn-secondary:hover::before
 {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.music-search-seed-submit
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.music-search-seed-submit::before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.music-search-seed-submit:hover::before {
  opacity: 1!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.chartlist-row:hover,
.dashbody .kerve .genretodaybar
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}


}

@-moz-document domain("last.fm") {
/* Image Settings */

.column-tracks-item-wrap,
.kerve .genretodayitem,
.kerve .spikewrap .bubble.show,
.music-artists-featured,
.music-featured-tag-item-wrap,
.music-more-artists-item-wrap,
.music-more-tags-tag-wrap,
.music-releases-item-wrap,

li
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.column-tracks-item-wrap:hover,
.kerve .genretodayitem:hover,
.kerve .spikewrap .bubble.show:hover,
.music-artists-featured:hover,
.music-featured-tag-item-wrap:hover,
.music-more-artists-item-wrap:hover,
.music-more-tags-tag-wrap:hover,
.music-releases-item-wrap:hover,

li:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("last.fm") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Heart ________________________________________________________________________________*/

/* On ___________________*/

a.chartlist-love-button:focus:before,
a.chartlist-love-button:hover:before,
button.chartlist-love-button:focus:before,
button.chartlist-love-button:hover:before
{
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________________*/

a.chartlist-love-button:before,
button.chartlist-love-button:before
{
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

a.chartlist-love-button:focus:before,
a.chartlist-love-button:hover:before,
button.chartlist-love-button:focus:before,
button.chartlist-love-button:hover:before,
a.chartlist-love-button:before,
button.chartlist-love-button:before
{
    background-size: 2rem!important;
    height: 2rem!important;
    padding-left: 2rem!important;
    width: 10px!important;
}

/* Play ______________________________________________________________________________*/

selector {
    content: "";
}

.chartlist-play-button:before,
.link-block .column-tracks-item-playlink,
.music-releases-item-playlink,
.stationlink-list .stationlink:before
{
	background: url(https://i.ibb.co/7VB6KK3/red-play.png) no-repeat!important;
	box-sizing: border-box;
}

.chartlist-play-button:hover:before,
.link-block .column-tracks-item-playlink:hover,
.music-releases-item-playlink:hover,
.stationlink-list .stationlink:hover:before
{
	background: url(https://i.ibb.co/qdS3kwB/red-play-hover.png) no-repeat!important;
	box-sizing: border-box;
}

.music-releases-item-playlink:hover,
.music-releases-item-playlink
{
	background-size: 65px!important;
	height: 66px!important;
	padding-left: 66px!important;
	width: 1px!important;
}

.chartlist-play-button:before,
.chartlist-play-button:hover:before,
.link-block .column-tracks-item-playlink,
.link-block .column-tracks-item-playlink:hover,
.stationlink-list .stationlink:before,
.stationlink-list .stationlink:hover:before
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.masthead-search-toggle:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.masthead-search-toggle:before
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}



}

@-moz-document domain("last.fm") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
