style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("kaskus.co.id") {
  .user-main-content h2 font[color="red"] b,
  .author.vcard .nickname font[color="red"] b,
  .author.vcard .user-info a:last-child font[color="red"] b,
  .user.vcard .user-details a.fn font[color="red"] b { display: none; }
  .user-main-content h2 font[color="red"]:after,
  .author.vcard .nickname font[color="red"]:after,
  .author.vcard .user-info a:last-child font[color="red"]:after,
  .user.vcard .user-details a.fn font[color="red"]:after {
    content: "[Rp]";
    font-weight: bold;
    display: inline-block;
  }
}
