	margin-left: -1px !important;
	margin-right: -1px !important}
#main-window[sizemode=normal][tabsontop] #TabsToolbar {
	margin-top: -1px !important}

/*Color and background*/
#main-window[tabsontop="true"] .tabbrowser-tab:not([selected="true"]) .tab-text:not(:-moz-lwtheme) {
  text-shadow: 0 0 10px /*[[shadowcolor]]*/,
                 0 0 10px /*[[shadowcolor]]*/!important;
}

/*Toolbar*/
#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme) > #nav-bar,
#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme):not([customizing]) > #nav-bar[collapsed="true"] + toolbar,
#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme):not([customizing]) > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar {
  background-image: -moz-linear-gradient(hsl(/*[[bkcolor]]*/,58%,95%), hsl(/*[[bkcolor]]*/,75%,/*[[inst]]*/))!important;
}

#navigator-toolbox[tabsontop="true"]:not(:-moz-lwtheme) > #PersonalToolbar,
#navigator-toolbox:not(:-moz-lwtheme) toolbar:not(#toolbar-menubar):not(#TabsToolbar):not(#nav-bar):not(#addon-bar), 
#addon-bar:not(:-moz-lwtheme),
.findbar-container:not(:-moz-lwtheme) {
  background-image: -moz-linear-gradient(hsl(/*[[bkcolor]]*/,75%,/*[[inst]]*/), hsl(/*[[bkcolor]]*/,75%,/*[[inst]]*/))!important;
}

#navigator-toolbox::after {
  background-color: #A9BBCE!important;
}

#main-window[tabsontop=true][disablechrome] #navigator-toolbox::after {
  background-image: -moz-linear-gradient(hsl(/*[[bkcolor]]*/,58%,95%), hsl(/*[[bkcolor]]*/,58%,95%))!important;
}

#main-window[sizemode="maximized"][tabsontop="true"][tabsintitlebar="true"] #TabsToolbar,
#main-window[sizemode="fullscreen"][tabsontop="true"][tabsintitlebar="true"] #TabsToolbar {
  margin-top: -1px;
}

.tabbrowser-tab {
  -moz-appearance: none!important;
  margin-bottom: 1px!important;
  padding: 0!important;
  border: 0 solid transparent!important;
  background: transparent!important;
  background: none!important;
  text-shadow: none;
}

.tab-throbber[pinned],
.tab-icon-image[pinned] {
  -moz-margin-start: 0px!important;
  -moz-margin-end: 0px!important;
}

.tab-content {
  margin-top: 0px !important;
  border: solid transparent!important;
  border-width: 0!important;
  padding: 4px 10px 2px!important;
  margin-left: -14px!important;
}
#tabbrowser-tabs > tab:first-child .tab-content {
  margin-left: -14px!important;
}


#nav-bar:not(:-moz-lwtheme),
#PersonalToolbar:not(:-moz-lwtheme),
#browser-bottombox:not(:-moz-lwtheme) {
  box-shadow: none !important;
}


#TabsToolbar:-moz-lwtheme {
  background-image: none!important;
}
 

#tabbrowser-tabs > .tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox > .scrollbox-innerbox {
  position: relative;
}

 
#main-window:not([sf-version]) #TabsToolbar:not([multibar="true"]):not([treestyletab-mode]) #tabbrowser-tabs > .tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox {
  margin-bottom: -1px;
}
 
#main-window:not([sf-version]) #TabsToolbar:not([multibar="true"]):not([treestyletab-mode]) {
  margin-bottom: 0px;
}
 
/* Tabs */
 
#tabbrowser-tabs[positionpinnedtabs] > .tabbrowser-tab[pinned] {
  min-height: 16px;
}
 

.tab-text {
  -moz-padding-start: 1px;
}
 
.tabbrowser-tab:not([selected]) .tab-text {
  text-shadow: 0 0 4px white;
  color: black;
}
 
#TabsToolbar:-moz-lwtheme-brighttext .tabbrowser-tab:not([selected]) .tab-text {
  color: rgba(255, 255, 255, 0.8);
  text-shadow: 0 0 3px black;
}
 
/* Black text for Aero Basic */
.tabs-newtab-button {
  -moz-margin-start: 2px !important;
  -moz-appearance: none;
  padding: 1px 5px 4px 0px!important;
  width: 32px; min-width: 60px;
  border-width: 0 27px 0 27px; border-style: solid; border-color: transparent;
}
 
#TabsToolbar .tabs-newtab-button:not([disabled]):hover {
  -moz-border-image: url("data:image/png;base64,") 0 27 0 27 / 0 27px 0 27px;
  border-image: url("data:image/png;base64,") 0 27 0 27 fill / 0 27px 0 27px;
}
 
.tabbrowser-tab .tab-stack {
  height: 31px; max-height: 31px;
  margin: 0 -12px 0; padding: 0 1px 2px;
  border-width: 0 27px 0 27px; border-style: solid; border-color: transparent;
}
 
/*Background*/
.tabbrowser-tab[selected="true"] .tab-stack {
  padding: 0 0 2px;
  position: relative;
  -moz-border-image: url("/*[[tabcolor]]*/")  0 28 0 28 / 0 28px 0 28px;
  border-image: url("/*[[tabcolor]]*/")  0 28 0 28 fill / 0 28px 0 28px;
  border-width: 0 28px 0 28px
}
 
.tabbrowser-tab:hover:not([selected="true"]) .tab-stack {
  -moz-border-image: url("data:image/png;base64,") 0 27 0 27 / 0 27px 0 27px;
  border-image: url("data:image/png;base64,") 0 27 0 27 fill / 0 27px 0 27px;
}
 
/*app tabs sure are fun */
.tabbrowser-tab[first-tab] .tab-stack {
  -moz-margin-start: 0px!important;
}
 
#TabsToolbar:-moz-lwtheme .tabbrowser-tab[selected="true"] .tab-stack {
  -moz-border-image: url("data:image/png;base64,") 0 28 0 28 / 0 28px 0 28px!important;
  border-image: url("data:image/png;base64,") 0 28 0 28 fill / 0 28px 0 28px!important;
  border-width: 0 28px 0 28px!important; border-style: solid!important;
}
 
.tabbrowser-tab[pinned][titlechanged][first-tab]:not([selected]):before {
  left: 12px!important;
}
 
.tabbrowser-tab[pinned][titlechanged]:not([selected]):before {
  content: " "!important;
  position: absolute!important; z-index: 1!important; top: 3px!important;
  width: 39px!important; height: 27px!important;
  background-image: url("data:image/png;base64,")!important;
  background-position: 1px 0!important;
  background-repeat: no-repeat!important;
}
 
.tab-throbber {
  list-style-image: url("data:image/png;base64,")!important;
}
 
.tab-throbber[progress] {
  list-style-image: url("data:image/png;base64,") !important;
}
 
.tabbrowser-tab[pinned] {
  min-height: 16px!important;
}
 
.tab-throbber[pinned],
.tab-icon-image[pinned] {
  -moz-margin-start: 2px!important;
  -moz-margin-end: 2px!important;
}
 
.tabbrowser-tab:not([selected="true"]):hover + .tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before,
.tabbrowser-tab[selected="true"] + .tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before {
  background-image: none!important;
}


/* Background tab separators (1px wide).
   Also show separators beside the selected tab when dragging it. */
#tabbrowser-tabs[movingtab] > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before,
#tabbrowser-tabs:not([overflow]) > .tabbrowser-tab[last-visible-tab]:not([selected]):not([beforehovered]):not(:hover)::after {
  -moz-margin-start: -1px!important;
  background-image: linear-gradient(rgba(10,31,51,0), rgba(10,31,51,0.3))!important;
  background-position: left bottom!important;
  background-repeat: no-repeat!important;
  background-size: 1px 100%!important;
  content: ''!important;
  display: inline-block!important;
  height: 27px!important;
  width: 1px!important;
  margin-top: -2px !important;
}

/* tabbrowser-tab focus ring */
.tabbrowser-tab:focus > .tab-stack {
  outline: 1px dotted;
}
 

/* Tab close button */
.tab-close-button {
  list-style-image: url(data:image/png;base64,)!important;
  -moz-image-region: rect(0, 72px, 18px, 54px)!important;
}

.tab-close-button:hover, .tab-close-button:hover[selected="true"] {
  -moz-image-region: rect(0, 36px, 18px, 18px)!important;
}

.tab-close-button:hover:active, .tab-close-button:hover:active[selected="true"] {
  -moz-image-region: rect(0, 54px, 18px, 36px)!important;
}

.tab-close-button[selected="true"] {
  -moz-image-region: rect(0, 18px, 18px, 0)!important;
}
 
/* Tab scrollbox arrow, tabstrip new tab and all-tabs buttons */
 
.tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down {
  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABYAAAAQCAYAAAAS7Y8mAAAAZElEQVQ4y7WU0QkAIAhEHc6N2h+zkBAr8D5OEMK6h1kqZiZu6j7CdcUQf+lP0MJQ+E8vOYjCK7To740O/AfNYEXhHU37IALdSygLJAHwiu2S8cGUUlAej/bdmA1Ca2nOEGKNzQlRmGd+30qSAAAAAABJRU5ErkJggg==") !important;
}
 
.tabbrowser-arrowscrollbox > .scrollbutton-down:-moz-locale-dir(ltr),
.tabbrowser-arrowscrollbox > .scrollbutton-up:-moz-locale-dir(rtl) {
  -moz-image-region: rect(0 11px 16px 0) !important;
}
 
.tabbrowser-arrowscrollbox > .scrollbutton-down:-moz-locale-dir(rtl),
.tabbrowser-arrowscrollbox > .scrollbutton-up:-moz-locale-dir(ltr) {
  -moz-image-region: rect(0 22px 16px 11px) !important;
}
 
.tabbrowser-arrowscrollbox > :-moz-any(.scrollbutton-up,.scrollbutton-down)[disabled="true"] {
  opacity: .4 !important;
}
 
.tabbrowser-arrowscrollbox > :-moz-any(.scrollbutton-up,.scrollbutton-down)[notifybgtab] {
  background-origin: border-box!important;
  background-image: -moz-radial-gradient(center, circle closest-side, hsla(205,100%,70%,.3), hsla(205,100%,70%,0))!important;
  list-style-image: url("data:image/png;base64,") !important;
}
