background: hsl(/*[[bkcolor]]*/,75%,60%)!important;
box-shadow: 0 -1px 0 0 black!important;
}

#main-window {
background: hsl(/*[[bkcolor]]*/,75%,60%)!important;
} }

#bookmarksPanel, #history-panel {
    background-color: hsl(/*[[bkcolor]]*/,75%,92%)!important;
    background: hsl(/*[[bkcolor]]*/,75%,92%)!important;
}

