style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rajanews.com") {
@font-face {
    font-family: "IRANSansWeb";
    font-style: normal;
    font-weight: normal;
    src: local('IRANSansWeb'), local('IRANSansWeb Regular'), local('IRANSansWeb-Regular'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb.eot'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb.eot?#iefix') format('embedded-opentype'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb.woff2') format('woff2'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb.woff') format('woff'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb.ttf') format('truetype');
}
@font-face {
    font-family: "IRANSansWeb";
    font-style: normal;
    font-weight: bold;
    src: local('IRANSansWeb Bold'), local('IRANSansWeb-Bold'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb_Bold.eot'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb_Bold.eot?#iefix') format('embedded-opentype'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb_Bold.woff2') format('woff2'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb_Bold.woff') format('woff'), url('http://72sq.com/wp-content/themes/72sq/fonts/IRANSansWeb_Bold.ttf') format('truetype');
}
.left-left,
.left-left object,
.left-left iframe,
.left-left img,
.left-left .txt_adv_content {
    display: none !important;
}
a[href*="samgroup.co"],
a[href*="nazr.ir"] {
    display: none !important;
}
*,
.node .content .body * {
    -webkit-font-smoothing: antialiased;
    text-rendering: geometricPrecision;
    word-spacing: normal !important;
    letter-spacing: normal !important;
    font-family: 'IRANSansWeb', "IRANSans", "Segoe UI", tahoma !important;
}
#NewsWithImage3 .Titr1 a,
#NewsWithImage3 .Titr1 a:link,
#NewsWithImage3 .Titr1 a:active,
#NewsWithImage3 .Titr1 a:visited,
#ShowNewsTitle a,
#ShowNewsTitle a:link,
#ShowNewsTitle a:active,
#ShowNewsTitle a:visited,
#NewsWithImage1 .Lead {
    font-size: 13px !important;
}
#NewsWithImage3 .Lead {
    font-size: 12px !important;
}
#NewsWithImage1 .Titr2 a,
#NewsWithImage1 .Titr2 a:link,
#NewsWithImage1 .Titr2 a:active,
#NewsWithImage1 .Titr2 a:visited {
    font-size: 14px !important;
}
}
