style.css:
/* O CORREDOR DAS ABAS */

#TabsToolbar toolbarseparator
{
-moz-appearance:none!important;
margin-bottom: -32px !important;
}


/* TIRA UMA ABA TRANSPARENT DECIMA DA MINHA ABA */


#TabsToolbar .tab-background {
    margin: 0 !important;
    background: transparent !important;
}

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    display: none !important;
}

#TabsToolbar .tab-background-middle {
    margin: -4px -2px !important;
    background: transparent !important;
}

#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}


/* ABA PRINCIPAL, A SUA CONSTRUÇÃO */ 
.tabbrowser-tab[selected="true"] 
{
min-width: 209px !important;
height: 40px !important;
border: 0.2px solid #000000 !important;
border-radius: 70px !important;
text-align: center !important;
margin-bottom: 4px !important;
margin-top: 3px !important;
margin-left: 3px !important; 
margin-right: 3px !important;
background: #000000 url("data:image/png;base64,") repeat-x center !important;
color: white !important;
font-size: 19px !important;
font-weight: bold !important;
text-shadow: 0px 0px 3px #aaa, 0px 0px 7px #aaa, 0px 0px 7px #aaa, 0px 0px 3px #aaa, 0px 0px 7px #aaa, 0px 0px 7px #aaa !important;
text-decoration: underline !important;
}

         /* ESTICA A ABA ACTIVA 
.tabbrowser-tab[selected="true"]:hover
{
border-radius: 15px !important;
-moz-transition-delay: 1.00s;
max-width: 289px !important;
min-width: 549px !important; 
} */

/* ABA PRINCIPAL, A SUA CONSTRUÇÃO, TEXTO MUDA DE FORMA E COR */ 
.tabbrowser-tab[selected="true"] .tab-text
{
margin-top: -5px! important;
}

  .tabbrowser-tab[selected="true"]:hover .tab-text 
{
font-style: italic !important;
color: #ffff00 !important;
}

/* ABA PRINCIPAL, A SUA CONSTRUÇÃO, BOTÃO FECHAR MUDA DE FORMA E COR ON HOVER */
.tabbrowser-tab[selected="true"] .toolbarbutton-icon
{
height: 25px !important;
width: 25px !important;
-moz-image-region: rect(16px, 16px, 32px, 0) !important;
  list-style-image: url("data:image/png;base64,%3D%3D") !important;
}

.tab-close-button:hover 
{
	list-style-image:url("chrome://global/skin/icons/close.png") !important;
-moz-appearance:none !important;
-moz-transform:rotate(70deg)!important;

}

            /* A SOMBRA CINZA DEBAIXO DA ABA */
.tabbrowser-tab[selected="true"]:hover, .tabs-newtab-button:hover {
  background-color: rgba(250,250,250,0.5) !important;
  box-shadow: rgba(130, 130, 130, 1.7) 0px 24px 8px 4px !important;
}

/* ABA PRINCIPAL, A SUA CONSTRUÇÃO, LOGOTIPO MAIOR */
.tabbrowser-tab[selected="true"] .tab-icon-image 
{
height: 29px !important;
width: 19px !important;
margin-left: 1px !important;
}

/*     FIM        ABA PRINCIPAL, A SUA CONSTRUÇÃO    FIM    */


            /*ABA SECUNDÁRIA, A SUA CONSTRUÇÃO */

.tabbrowser-tab:not([selected="true"]) 
{

  min-width: 147px !important;
/*max-width: 99px !important; */
  
border-radius: 100px !important;
border: 1px solid !important;
text-align: center !important;
margin-bottom: 7px !important;
margin-top: 7px !important;
margin-left: 2px !important;
padding-left: 2px!important; /* << ESTE EMPURRA O BOTÃO FECHAR PARA A  DIREITA*/
font-style: italic !important;
}

/*
.tabbrowser-tab:not([selected="true"]):hover
{

-moz-transition: background-color .9s ease-in,  border-color .9s ease-in,  box-shadow .9s ease-in !important;
border-radius: 5px 95px 95px 5px !important;
-moz-transition-delay: 3.50s;
max-width: 289px !important;
min-width: 300px !important; */



/*ABA SECUNDÁRIA, A SUA CONSTRUÇÃO, PÕE AS ABAS COM UMA COR ASSOMBREADA*/
.tabbrowser-tab:not([selected="true"]), .tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down {
background-image: -moz-linear-gradient(rgba(255,255,255,0.75), rgba(127,127,127,0.175) 53px, rgba(0,0,0,0.125) 50%, rgba(0,0,0,0.125), rgba(0,0,0,0.125) 60%, rgba(255,255,255,0.625) 98.5%) !important;
}

.tabbrowser-tab:not([selected="true"]):hover, .tabbrowser-arrowscrollbox > .scrollbutton-up:hover,
.tabbrowser-arrowscrollbox > .scrollbutton-down:hover {
background-image: none !important;
}

/*ABA SECUNDÁRIA, A SUA CONSTRUÇÃO, MODIFICA OS TEXTOS ON HOVER*/

.tabbrowser-tab:not([selected]) .tab-text 
{
margin-top: -2px! important;
margin-left: -1px! important;
}
.tabbrowser-tab:not([selected]):hover .tab-text 
{
text-decoration: underline;
color: #CC00CC !important;
}

/*ABA SECUNDÁRIA, A SUA CONSTRUÇÃO, MODIFICA O LOGO*/
.tabbrowser-tab:not([selected="true"]) .tab-icon-image 
{
height: 17px !important;
width: 17px !important;
margin-right: 4px! important;
}

/*ABA SECUNDÁRIA, A SUA CONSTRUÇÃO, MODIFICA O BOTÃO DE FECHAR */
.tabbrowser-tab:not([selected="true"]) .toolbarbutton-icon
{
height: 23px !important;
width: 23px !important;
  list-style-image: url("data:image/png;base64,") !important;
}
tab .tab-close-button, 
.tabs-closebutton, 
#find-closebutton {
  -moz-image-region: rect(16px, 16px, 32px, 0) !important;

}

/*ABA SECUNDÁRIA, A SUA CONSTRUÇÃO, O AZUL POR BAIXO */
.tabbrowser-tab:not([selected="true"]):hover,
.tabs-alltabs-button:active,
.tabs-newtab-button:hover,
.tabbrowser-arrowscrollbox > .scrollbutton-up:hover, 
.tabbrowser-arrowscrollbox > .scrollbutton-down:hover
 {
  color: buttontext !important; 
  box-shadow: highlight /*( highlight )*/ 2px 14px 9px -3px !important; 
}

/*CURSORES DEDO APONTADOR NOS BOTÕES NA ABA SECUNDÁRIA*/
tabbrowser tab .tabs-closebutton,
tabbrowser tab .tab-close-button:hover,
.tabbrowser-tab:not([selected="true"]):hover
{
cursor:url("data:image/x-icon;base64,AAACAAEAICAQAAUAAADoAgAAFgAAACgAAAAgAAAAQAAAAAEABAAAAAAAAAAAAAAAAAAAAAAAEAAAAAAAAAAAAAAAAACAAACAAAAAgIAAgAAAAIAAgACAgAAAwMDAAICAgAAAAP8AAP8AAAD%2F%2FwD%2FAAAA%2FwD%2FAP%2F%2FAAD%2F%2F%2F8AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAP%2F%2F%2F%2F8AAAAAAAAAAAAAAAD%2F%2F%2F%2F%2F8AAAAAAAAAAAAAAP%2F%2F%2F%2F%2F%2FAAAAAAAAAAAAAA%2F%2F%2F%2F%2F%2F%2F%2FAAAAAAAAAAAAD%2F%2F%2F%2F%2F%2F%2F%2FwAAAAAAAAAAAP%2F%2F%2F%2F%2F%2F%2F%2F8AAAAAAAAAAAD%2F%2F%2F%2F%2F%2F%2F%2F%2FAAAAAAAAAAAA%2Fw%2F%2F%2F%2F%2F%2F%2FwAAAAAAAAAAAP8P%2F%2F%2F%2F%2F%2F8AAAAAAAAAAAD%2FD%2FD%2FD%2FD%2FAAAAAAAAAAAA%2Fw%2Fw%2Fw%2Fw%2FwAAAAAAAAAAAAAP8P8P8P8AAAAAAAAAAAAAD%2FD%2FD%2FAAAAAAAAAAAAAAAA%2Fw%2FwAAAAAAAAAAAAAAAAAP8AAAAAAAAAAAAAAAAAAAD%2FAAAAAAAAAAAAAAAAAAAA%2FwAAAAAAAAAAAAAAAAAAAP8AAAAAAAAAAAAAAAAAAAD%2FAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAD%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2F%2FAD%2F%2F%2FwA%2F%2F%2F8AH%2F%2F%2BAB%2F%2F%2FAAP%2F%2FgAD%2F%2FwAA%2F%2F8AAP%2F%2FAAD%2F%2FwAA%2F%2F8AAP%2F%2FAAD%2F%2F4AA%2F%2F%2FgAf%2F%2F4A%2F%2F%2F%2BB%2F%2F%2F%2Fh%2F%2F%2F%2F4f%2F%2F%2F%2BH%2F%2F%2F%2Fh%2F%2F%2F%2F8%2F%2F%2F%2Fw%3D%3D"),pointer !important;
}
.tabbrowser-tab:not([selected="true"]):hover,
.tabbrowser-tabs tab .tab-text :hover
{
  cursor: pointer !important; 
}
         /* O BOTÃO FECHAR SEMPRE NA 1ª */
.tab-close-button
{
visibility: visible !important;
display: inline !important;
}

         /*PÕE AS ABAS A VER SE BEM*/
.tabbrowser-tabs *|tab {    
   opacity: 1 !important;
 }


