/*A SETA AMARELA ENTRE O ARRASTAR DAS TABS*/
.tab-drop-indicator 
{  
width: 0 !important;
height: 21px !important;
padding-left: 18px !important;
margin-top: -18px !important;
margin-bottom: 18px !important;
background: url(data:image/vnd.microsoft.icon;base64,) center no-repeat !important; 
}

 /* TABS PARA BAIXO DO SEARCHBAR */

#TabsToolbar
{
  -moz-box-ordinal-group:101 !important;
}

     /* ABAS LIDAS, NÃO LIDAS E ACTIVAS*/
.tabbrowser-tab:not([unread="true"]):not([selected="true"]) {
   color: #0000EE !important; 
}

/* Change underline color of active tab  - RED */
.tabbrowser-tab[selected="true"] {
   color: #FFF !important; }

/* Change text color of loading/reloading tab - Magenta */
.tabbrowser-tab[unread="true"]{
   color: #CC0000 !important; }
    
       /* O X NAS ABAS */
.tab-close-button, 
.tabs-closebutton, 
#find-closebutton {
  list-style-image: url("data:image/png;base64,") !important;
  -moz-image-region: rect(0, 16px, 16px, 0) !important;
}

tab[selected="true"] .tab-close-button, 
.tabs-closebutton, 
tab:hover .tab-close-button, 
#find-closebutton {
  -moz-image-region: rect(16px, 16px, 32px, 0) !important;
}

tab .tab-close-button:hover, 
.tabs-closebutton:hover, 
#find-closebutton:hover {
  -moz-image-region: rect(32px, 16px, 48px, 0) !important;
}

/*APARECE AS SETAS AO LADO DAS ABAS */

.scrollbutton-up,
.scrollbutton-down
{
-moz-image-region: rect(1px 80px 16px 64px) !important;
border-radius: 5px !important;
border: 2px solid !important;
min-width: 39px !important;
-moz-appearance: tab !important;
-moz-border-left-colors: #ff0000 !important;
-moz-border-right-colors: #ff0000 !important;
-moz-border-top-colors: #ff0000 !important;
-moz-border-bottom-colors: #ff0000 !important;
}
.scrollbutton-up:hover,
.scrollbutton-down:hover
{
-moz-image-region: rect(1px 80px 16px 64px) !important;
border: 2px solid !important;
width: 60px !important;
-moz-appearance: button !important;
-moz-border-left-colors: #ff0000 !important;
-moz-border-right-colors: #ff0000 !important;
-moz-border-top-colors: #ff0000 !important;
-moz-border-bottom-colors: #ff0000 !important
}

       /*O DESENHO DAS SETAS AO LADO DAS ABAS*/

.tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down 
{
 margin-top: -2px !important;
  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABYAAAAQCAYAAAAS7Y8mAAAAZElEQVQ4y7WU0QkAIAhEHc6N2h+zkBAr8D5OEMK6h1kqZiZu6j7CdcUQf+lP0MJQ+E8vOYjCK7To740O/AfNYEXhHU37IALdSygLJAHwiu2S8cGUUlAej/bdmA1Ca2nOEGKNzQlRmGd+30qSAAAAAABJRU5ErkJggg==") !important;
}
.tabbrowser-arrowscrollbox > .scrollbutton-down:-moz-locale-dir(ltr),
.tabbrowser-arrowscrollbox > .scrollbutton-up:-moz-locale-dir(rtl) {
  -moz-image-region: rect(0 11px 16px 0) !important;
-moz-transform:rotate(0deg)!important;
}
.tabbrowser-arrowscrollbox > .scrollbutton-down:-moz-locale-dir(rtl),
.tabbrowser-arrowscrollbox > .scrollbutton-up:-moz-locale-dir(ltr) {
  -moz-image-region: rect(0 22px 16px 11px) !important;
}


          /* O BOTÃO DA NOVA ABA */ 

.tabs-newtab-button, #new-tab-button
{
 
display: none;
}

   /* NÃO APAGAR SOBE UMA PORCARIA QUE APARECE NO BOTÃO */
#TabsToolbar #new-tab-button label 
{
padding: 4px !important;
}

          /* A NEW TAB BUTTON ESTICA SE A VONTADE */
.tabs-newtab-button
{
-moz-box-flex: 109 !important;
min-width: 55px !important;

} 


/*A ABA AO LADO DA NEW TAB BUTTON COM NUMERAÇÃO*/
#TabsToolbar .tabs-alltabs-button, #tabcounter-toolbar-item 
{
font-size:17px; 
font-weight:600 !important; 
margin-bottom: 2px !important;
margin-top: 2px !important;
margin-left: -1px !important;
margin-right: -1px !important;;
border-radius: 5px !important;
border: 2px solid #CC0000 !important;
  text-shadow: black 1px -1px 2px, white -1px 1px 2px ,white -1px 1px 1px !important;
box-shadow: inset 8px -8px 16px -8px black,inset -8px 8px 16px -8px white, -8px 8px 16px -8px black !important;
background-color: #cccc00 !important; 
color: #CC0000 !important;						 
}

/* THIS REVEALS List all tabs BUTTON */
#main-window > #tab-view-deck > #browser-panel > #navigator-toolbox > #TabsToolbar > #alltabs-button {
    visibility: visible !important;
}
/* #main-window > #tab-view-deck > #browser-panel > #navigator-toolbox > #TabsToolbar > #tabbrowser-tabs:not([overflow="true"]) ~ #alltabs-button {
    visibility: collapse !important;
}
/**/

/* THIS SHOWS TABS COUNTER IN List all tabs BUTTON ONLY IF PLACED ON TAB BAR OR ADDON BAR **/
#main-window > #tab-view-deck > #browser-panel > #navigator-toolbox { 
    counter-reset: tabs !important;
} 
#main-window > #tab-view-deck > #browser-panel > #navigator-toolbox > #TabsToolbar > #tabbrowser-tabs .tabbrowser-tab[style="display: none;"] {
    display: block !important;
    visibility: collapse !important;
}
#main-window > #tab-view-deck > #browser-panel > #navigator-toolbox > #TabsToolbar > #tabbrowser-tabs .tabbrowser-tab {
    counter-increment: tabs !important;
}
#main-window:not([inDOMFullscreen]) > #tab-view-deck > #browser-panel > #navigator-toolbox > #TabsToolbar:not([style="visibility: collapse;"]) > #alltabs-button:after,
#main-window:not([inDOMFullscreen]) > #tab-view-deck > #browser-panel > #browser-bottombox > #addon-bar > #alltabs-button:after {
    content: counter(tabs) !important;
    display: -moz-box !important;
    font-weight: bold !important;
}
/* THIS HIDES DROP DOWN ARROW IN List all tabs BUTTON */
#main-window > #tab-view-deck > #browser-panel > #navigator-toolbox > #TabsToolbar:not([customizing]) > #alltabs-button > :-moz-any(label, image),
#main-window > #tab-view-deck > #browser-panel > #browser-bottombox > #addon-bar:not([customizing]) > #alltabs-button  > :-moz-any(label, image) {
    display: none !important;
}
/**/

/* THIS HIDES Tab Groups (PANORAMA) ITEM FROM List all tabs MENU */
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup #menu_tabview,
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup #alltabs-popup-separator {
    display: none !important;
}
/**/

/* THIS HIDES Undo Last Closed Tab ITEM FROM List all tabs MENU */
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup #alltabs_undoCloseTab {
    display: none !important;
}
/**/

/* THIS SHOWS NUMBERING IN THE List all tabs POPUP MENU THAT DON'T INCLUDE PINNED TABS */
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup { 
    counter-reset: latabs !important;
}
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup .menuitem-iconic.alltabs-item.menuitem-with-favicon {
    counter-increment: latabs !important;
}
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup .menuitem-iconic.alltabs-item.menuitem-with-favicon:before {
    content: counter(latabs) !important;
    min-width: 16px !important;
    display: -moz-box !important;
    padding-right: 4px !important;
}

/* THIS SETS List all tabs MENU MAXIMUM WIDTH */
#main-window > #tab-view-deck > #browser-panel #alltabs-button > #alltabs-popup .menuitem-iconic.alltabs-item.menuitem-with-favicon {
    max-width: 750px !important;
}

.tabs-closebutton-box,
.close-button.tabs-closebutton 
{
display:none;
}
