style.css:
@-moz-document regexp(".*(wordpress.com/wp-admin/).*") {

/* Set background to black for a darker theme.*/
html, #adminmenuback {
background-color:black !important;
}

/* Spruce up the heading (e.x. the word Dashboard) in green, with a white outline for readability.*/
h2 {
color:#018701 !important;
text-outline:2px white !important;
}

/* Modify left admin-menu's submenus (orange-ish background). */
#adminmenu .wp-has-submenu ul{
background-color:#EFC700 !important;
}

/* Make QuickPress, Recent Draft, Table column, etc. message text readable. */
.postbox .inside, td[class*="column"], td[class*="column"] p{
color:black !important;
}

/* Make Recent Comment, Your Stuff, Posts, etc. lists readable. */
.postbox .published, #the-comment-list, #the-list {
background-color:#278A1E !important;
color:white !important;
}

/* Alternate colours in Recent Comments, Your Stuff, etc. lists. */
.postbox .published.alt, #the-comment-list .alternate, #the-list .alternate {
background-color:#0A6D10 !important;
color:white !important;
}


/* Default colour for links in the main wpcontent area. */
#wpcontent a {
color:#D68800 !important;
}

}
