style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(kaskus.co.id) {
  .wrap {
    background-image: url(http://kkcdn-static.kaskus.co.id/images/2013/04/01/497260_20130401031703.png) !important;
  }

  .wrap.op2 {
    background-image: url(http://kkcdn-static.kaskus.co.id/images/2013/04/01/497260_20130401031734.png) !important;
  }

  .wrap.op3 {
    background-image: url(http://kkcdn-static.kaskus.co.id/images/2013/04/01/497260_20130401031818.png) !important;
  }

  .wrap,
  .wrap.op2,
  .wrap.op3 {
    background-position: 50% 40% !important;
    width: 800px !important;
    height: 600px !important;
  }

  .wrap p,
  .wrap.op2 p,
  .wrap.op3 p {
    visibility: hidden;
  }

  .wrap p:before,
  .wrap.op2 p:before,
  .wrap.op3 p:before,
  .wrap p:after,
  .wrap.op2 p:after,
  .wrap.op3 p:after {
    text-indent: 0 !important;
    color: #239da8;
    text-align: center !important;
    height: 0;
    position: absolute;
    left: 0;
    right: 0;
    visibility: visible;
    text-shadow: 2px 2px 0px #fff;
  }

  .wrap p:before,
  .wrap.op2 p:before,
  .wrap.op3 p:before {
    content: "KASKUS is over posting.";
    font-size: xx-large !important;
    font-weight: bold !important;
    bottom: 4em;
  }

  .wrap p:after,
  .wrap.op2 p:after,
  .wrap.op3 p:after {
    content: "Please wait a moment and try again.";
    font-size: large !important;
    bottom: 4.5em;
  }

  .wrap.op3 h1 {
    visibility: visible !important;
  }

  .wrap a,
  .wrap.op2 h1 a,
  .wrap.op3 h1 a {
    top: 25px !important;
    left: 0 !important;
    right: 0 !important;
    z-index: 100 !important;
    margin: auto !important;
    padding: 1em 5em .5em !important;
  }
}
