style.css:
@-moz-document domain("opengl-tutorial.org") {
#sideBar, #branding
{
  display:none!important;
}

body
{
  background-color:white !important;
  margin:10px;
}

body, a, h1, h2, h3, h4, h5, h6, pre
{
 color:black !important; 
}

#main
{
  width:100%;
}

#wrapper
{
  margin:0;
  padding:0;
  width:100%;
}

#content
{
  margin:0;
}

.syntaxhighlighter
{
  background:white!important;
  border:1px solid black!important;
  padding:1px 0;
}

.syntaxhighlighter .toolbar
{
  display:none!important;
}

#jquery_toc
{
  background-color:white;
  border:1px solid black;
}

.syntaxhighlighter:before
{
  font-weight:bold;
  display:block;
  border-bottom:1px solid black;
  padding:0 5px;
  margin-bottom:3px;
}

.syntaxhighlighter.c:before {content:"C++"}
.syntaxhighlighter.cpp:before {content:"C++"}
.syntaxhighlighter.fs:before {content:"GLSL Fragment Shader"}
.syntaxhighlighter.vs:before {content:"GLSL Vertex Shader"}

#access li a
{
  background-color:silver!important;
  border:1px solid black!important;
}

#access li a:hover
{
  background-color:white!important;
}
}
