style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.lesroyaumes.com") {
    .cadre_interieurPub, .ensemble .cadre_interieur {
        width:1098px!important;
    }
    
    .cadre_interieurPub .colonneContenu{
        width:800px!important;
    }
    
    .zonePubDroiteContenant {
        margin-left: 0px !important;
        width: 125px !important;
        background: none !important;
    }
    
     .zonePubDroite {
        margin-left: 0px !important;
        margin-right: 0px !important;
        background: none !important;
        position: relative;
        right: -15px;
    }
    
    .zonePubDroiteContenant .zone_menu, .zonePubDroite .zone_menu {
        padding: 10px 0 10px 10px;
        border-radius: 6px 0px 0px 6px !important;
        width: 135px !important;
        margin-right: 0px !important;
 
    }
    
    .espace_pub_contenu_2{
        display:none!important;
    }
    .zone_caracteristiques .caracteristique {
        font-size: 12px!important;
    }
    .horloge {
        top: -2px;
    }
    
    .zonePubDroite {
        display: block !important;
    }
}
