style.css:
@-moz-document url-prefix("https://medium.com/") {
html, body, .container {
  height: auto;
  overflow: visible;
}
.container-surface {
  position: static;
  height: auto;    
}
.screen-content {
  height: auto;
  overflow: visible;      
}
.site-nav .site-nav-logo {
  position: fixed;          
}
}
