@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("performancemanager4.successfactors.com") {
   #floatingMenu {
       position: absolute;
       top: 650px;
       left: 52px;
       width: 170px;
       background-color: #cdcdcd;
       border: black 1px solid;
       padding: 5px;
       margin: 0;
       text-align: center;
       z-index:9999;
   }
   #floatingMenu h1 {
       font-size: 110%;
