style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mixi.jp") {

html { background-color: #EEE !important;}

body {
margin:0 auto !important;
padding:0 5px !important;
width:950px !important;
background: #FFD79F !important;
border-left: 1px solid #CCC !important;
border-right: 1px solid #CCC !important;
}

div.adBanner iframe,
div#adBanner,
div.prContents,
div#musicTeaserBannerArea 
{ display:none !important;}

body,
#bodyArea,
#footerArea,
#oldMain
#wrapper { background: url(http://img.mixi.jp/img/bg_line.gif) repeat #FFF !important;}

#mixiNewsArea { margin-bottom:5px !important;}

div.contents,
.newListBox01,
.newListBox02 { background-color: #FFFFFF !important; border:1px solid #CCC !important;}

.bodySubSection, .leftbox { background-color: #FFFFFF !important;}

#footprintjack,
#footprintjack02 {
display: none !important;
}

.help,
.logout {
padding:0 5px !important;
border:2px solid #FFC76F !important;
background-color:#FFDA9F !important;
}

.utilityNavigation01 {
margin:0 5px 0 0 !important;
}

.submit a {
text-indent:-9999px !important;
}

}
