style.css:
@-moz-document domain(tumblr.com) {
#logo {height: 0 !important;
        width: 0 !important;
	padding-left: 187px !important;
	padding-top: 48px !important;
	background: url("http://i34.tinypic.com/a14qoo.png") no-repeat !important;}

#home_button {

    padding-right: 0px !important;
}


#home_button a {

    height: 0px !important;
    width: 35px !important;
    top: 3px !important;
    padding-left: 0px !important;
    padding-top: 37px !important;
    background: url("http://i34.tinypic.com/zbo7p.png") !important;
}

#home_button .tab_notice {
    left: 35px !important;
    right: auto !important;
}

#logout_button {

    padding-right: 10px !important;
}


#logout_button a {
    background-image: none !important;
    position: relative !important;
    left: -1px !important;
    top: -2px !important;
    width: 24px !important;
    padding-left: 10px !important;
    background: url("http://i34.tinypic.com/2j2uej4.png") 
!important;
    background-size: 35px 40px !important; 
}


.new_post_label .new_post_label_icon {
background-image: url('http://i35.tinypic.com/5mil8z.png') !important;
margin-top: -5px !important;
background-size: 520px 190px !important;
opacity: .7 !important;
}

#posts .post.new_post {
background-image: url('http://i36.tinypic.com/2ebh0qr.png') !important;
background-position: top right!important;
background-repeat: no-repeat, repeat !important;
}

#posts .post.new_post .arrow {
background: transparent url('http://i35.tinypic.com/dwchu9.png') !important;
background-repeat: no-repeat !important;
}

div.l-content #new_post .new_post_label {
line-height: 2.8em !important;
text-transform: lowercase !important;
font-size: .9em !important;
}
}
