style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://theoldreader.com/"){

/* hide top serch bar*/
nav.navbar-fixed-top:not(:hover){
    margin-top:-50px !important;
    }
div.main-container,
div.main-row div.cell{
    margin-top:-50px !important;
    }
    
/* hide side menu bar*/
/*
div#sidebar:not(:hover){
    margin-left:-220px !important;
}
div#main{
    left:0 !important;
    width:100%; !important;
    z-index:0 !important;
}
*/

/* side menu tweak */
ul.nav li.sort-item {
    margin-left:15px !important;
    }
/*
div.sort-item:not(:hover) ul.feeds-list{
    display:none !important;
    }
*/

/* compact and font size */
li.social{
    display:none !important;
    }
li.nav-header{
    padding-top:0px !important;
    margin-top:0px !important;
    height:18px;
    }
li.sort-item{
    padding:0px !important;
    }
span.badge{
    font-size:10px !important;
    }
div.floating{
    padding:0px !important;
    }
body{
    line-height:1.1em !important;
    }
h3{
    font-size:14px !important;
    line-height:1.1em !important;
    }
div.content-body{
    font-size:11px !important;
    }
div.content-body p{
    line-height:1.1em !important;
    }

/* bottuns no label */
    div.btns a span{
        display:none !important;
    }
}
