style.css:
@-moz-document url-prefix("http://bc-city.com") {
/**
 * @package userCSS style for bc-city.com
 * @version 0.2
 * @author SHaRK
 */

/* фон страницы --- начало */
html {
	background-color:#505E6F;
	background-image:url("/*[[bkg]]*/");
}

body#ipboard_body {
	background:none !important;
	padding:30px 0 30px 0 !important;
}
/* фон страницы --- конец */



/* ссылка "последнее сообщение" --- начало */
ul.last_post.ipsType_small > li.desc.lighter.blend_links > a,a[href*='getlastpost'] {
	padding-right:20px;
	background-image:url("http://bc-city.com/public/style_images/master/last_post.png");
	background-attachment:scroll;
	background-position:95% 50%;
	background-clip:border-box;
	background-origin:padding-box;
	background-size:auto;
	background-repeat:no-repeat;
	font-size:13px !important;
	line-height:2 !important;
}

ul.last_post.ipsType_small > li > a[class*='url'] > span {
	color:#A4A4A4 !important;
	font-size:12px !important;
}

ul.last_post.ipsType_small > li > a[class*='url'] > span:hover {
	color:#AF286D !important;
}
/* ссылка "последнее сообщение" --- конец */



/* списки тем форума --- начало */
table#forum_table > tbody > tr[class*='recordRow']:hover,
div.ipsBox_container > table.ipb_table > tbody > tr:hover,
tr[class*='recordRow'].unread:hover {
	background:rgb(246,249,250);
	background:-moz-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(246,249,250,1)),color-stop(54%,rgba(239,244,247,1)),color-stop(100%,rgba(230,238,244,1)));
	background:-webkit-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:-o-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:-ms-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:linear-gradient(to bottom,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
}

td.col_f_views {
	border:1px solid #fff !important;
	background:rgb(246,249,250);
	background:-moz-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:-webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(246,249,250,1)),color-stop(54%,rgba(239,244,247,1)),color-stop(100%,rgba(230,238,244,1)));
	background:-webkit-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:-o-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:-ms-linear-gradient(top,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
	background:linear-gradient(to bottom,rgba(246,249,250,1) 0%,rgba(239,244,247,1) 54%,rgba(230,238,244,1) 100%);
}

td > span > a.url > span { /* подсветка ников в списках тем */
	color:#AF286D !important;
}

td.col_f_icon.short.altrow {
	padding-top:19px !important;
}
/* списки тем форума --- конец*/



/* боковое меню фильтров и списки тем --- начало */
.ipsBox {
	border-radius:8px;
}

div#content > div.ipsLayout.ipsLayout_withleft > div.ipsLayout_left {
	z-index:999 !important;
	float:right !important;
	margin-top:-25px !important;
	padding:0 !important;
	width:auto !important;
}

div#content > div.ipsLayout.ipsLayout_withleft > div.ipsLayout_content {
	float:right !important;
}

div#content > div.ipsLayout.ipsLayout_withleft.clearfix {
	padding:0 !important;
}

div#content > div.ipsLayout.ipsLayout_withleft > div.ipsLayout_left:hover {
	z-index:999 !important;
	border-radius:8px;
	-webkit-box-shadow:0px 0px 10px 10px rgba(50,50,50,0.3);
	-moz-box-shadow:0px 0px 10px 10px rgba(50,50,50,0.3);
	box-shadow:0px 0px 10px 10px rgba(50,50,50,0.3);
	cursor:pointer;
}

div#content > div.ipsLayout.ipsLayout_withleft > div.ipsLayout_left > div.ipsBox > div.ipsBox_container.ipsSideMenu {
	overflow:hidden;
	width:150px !important;
	height:10px !important;
	opacity:0.3 !important;
}


div#content > div.ipsLayout.ipsLayout_withleft > div.ipsLayout_left > div.ipsBox > div.ipsBox_container.ipsSideMenu:hover {
	z-index:1 !important;
	width:190px !important;
	height:auto !important;
	opacity:1 !important;
}

div#content > div.ipsBox.ipsVerticalTabbed.ipsLayout.ipsLayout_withleft.ipsLayout_tinyleft > div#mlist_tabs.ipsVerticalTabbed_tabs.ipsVerticalTabbed_minitabs.ipsLayout_left {
	float:left !important;
	margin-top:40px !important;
	-webkit-box-shadow:none;
	-moz-box-shadow:none;
	box-shadow:none;
}

div#content > div.ipsBox.ipsVerticalTabbed.ipsLayout.ipsLayout_withleft.ipsLayout_tinyleft > div.ipsVerticalTabbed_content.ipsLayout_content {
	float:left !important;
	margin-top:10px !important;
	margin-bottom:10px !important;
	width:1140px !important;
}

div#content > div.ipsBox.ipsVerticalTabbed.ipsLayout.ipsLayout_withleft.ipsLayout_tinyleft > div.ipsVerticalTabbed_content.ipsLayout_content > div.maintitle.ipsFilterbar.clear.clearfix {
	padding-left:10px !important;
}

div#content > div.ipsBox.ipsVerticalTabbed.ipsLayout.ipsLayout_withleft.ipsLayout_tinyleft > div#mlist_tabs.ipsVerticalTabbed_tabs.ipsVerticalTabbed_minitabs.ipsLayout_left {
	margin-left:10px !important;
}

div#content > div.ipsLayout.ipsLayout_withleft > div.ipsLayout_content {
	margin-top:30px !important;
}

span.post_id.right.ipsType_small.desc.blend_links {
	padding:5px;
	border:1px solid #BBB;
	border-radius:5px;
	background:#E2E9F0;
	line-height:15px;
	margin-top:4px;
}

span.post_id.right.ipsType_small.desc.blend_links:hover {
	border:1px solid #EEE;
	background:#E2E9F0;
	-webkit-box-shadow:1px 1px 5px 1px rgba(50,50,50,0.3);
	-moz-box-shadow:1px 1px 5px 1px rgba(50,50,50,0.3);
	box-shadow:1px 1px 5px 1px rgba(50,50,50,0.3);
}
/* боковое меню фильтров и списки тем --- конец */



/* фиксация блоков страницы --- начало */
div#content,
div#footer_utilities {
	margin:0 auto !important;
	width:1200px !important;
}

div#header_bar {
	margin:0 auto !important;
	padding:2px 10px 0 0 !important;
	width:1210px !important;
	border-radius:15px 15px 0 0!important;
}

div#footer_utilities,
div#ipsDebug_footer {
	padding:15px !important;
	width:1190px !important;
	background-color:rgba(255,255,255,0.6) !important;
}

div#ipsDebug_footer {
	margin:0 auto !important;
	border-radius:0 0 15px 15px !important;
}

div#branding {
	margin:0 auto !important;
	width:1220px !important;
	box-shadow:0px 5px 9px rgba(0,0,0,0.3);
}

div#primary_nav {
	margin:0 auto !important;
	padding:-20px !important;
	width:1220px !important;
}

div#header_bar > div.main_width,
div#branding > div.main_width {
	width:99% !important;
}

div#primary_nav > div.main_width {
	width:100% !important;
}
/* фиксация блоков страницы --- конец */



/* статистика юзера в теме --- начало */
li.group_icon,
p.desc.member_title,
span.ft.reputation,
ul.custom_fields > li > span.ft,
div.ipsAd {
	display:none !important;}

span.fc > img[src*='profile/f.png'],
span.fc > img[src*='profile/m.png'],
div#conversation span.fc > img[src*='profile'] {
	position:absolute !important;
	top:10px !important;
	left:65px !important;
}

div.post_wrap span.author.vcard {
	position:absolute !important;
	top:0 !important;
	left:89px !important;
}

div.post_wrap > h3 > span.ipsBadge.ipsBadge_green {
	margin-bottom:4px;
	font-size:10px;
}

div.post_wrap > h3 {
	background: #D8DDE8 url("http://copypaster.org/bccity/bkg/offline.png") !important;
	background-repeat: no-repeat !important;
  	background-position:9px 9px !important;
}

div.post_wrap div.reputation {
	position:absolute !important;
	top:35px !important;
	left:20px !important;
	border:1px solid #FFF !important;
	border-radius:5px !important;
	-webkit-box-shadow:1px 1px 3px 0px rgba(50,50,50,0.4);
	-moz-box-shadow:1px 1px 3px 0px rgba(50,50,50,0.4);
	box-shadow:1px 1px 3px 0px rgba(50,50,50,0.4);
	cursor:help;
}
/* статистика юзера в теме --- конец */



/* аватарки --- начало */
img.ipsUserPhoto {
	border-radius:5px !important;
}

img.ipsUserPhoto.ipsUserPhoto_large {
	border-radius:10px !important;
}

img.ipsUserPhoto.ipsUserPhoto_mini {
	margin:-2px 10px 0 0 !important;
	width:50px !important;
	height:50px !important;
	border-radius:100px !important; /* уменьшить до 10px для квадратных аватаров */
}

img.ipsUserPhoto.ipsUserPhoto_medium {
	margin:-2px 0 0 0 !important;
	width:55px !important;
	height:55px !important;
	border-radius:100px !important; /* уменьшить до 10px для квадратных аватаров */
}
/* аватарки --- конец */



/* фиксация ширины изображений --- начало */
div.post.entry-content > p > span > img.bbc_img {
	max-width:800px !important;

}

a.bbc_url img.bbc_img {
	margin-top:-6px !important;
}
/* фиксация ширины изображений --- конец */



/* подсветка заголовоков цитат --- начало */
div.post.entry-content > p.citation {
	background: rgb(234,234,234);
	background: -moz-linear-gradient(top,rgba(234,234,234,1) 0%,rgba(218,218,218,1) 100%);
	background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(234,234,234,1)),color-stop(100%,rgba(218,218,218,1)));
	background: -webkit-linear-gradient(top,rgba(234,234,234,1) 0%,rgba(218,218,218,1) 100%);
	background: -o-linear-gradient(top,rgba(234,234,234,1) 0%,rgba(218,218,218,1) 100%);
	background: -ms-linear-gradient(top,rgba(234,234,234,1) 0%,rgba(218,218,218,1) 100%);
	background: linear-gradient(to bottom,rgba(234,234,234,1) 0%,rgba(218,218,218,1) 100%);
}

div > div.post_body > div.post.entry-content > p.citation > a {
	float:left !important;
	margin-right:10px !important; 
}
/* подсветка заголовоков цитат --- конец */



/* кнопка перехода наверх --- начало */
div#footer_utilities.main_width.clearfix.clear > a#backtotop {
	position:fixed !important;
	top:30% !important;
	left:auto !important;
	margin-left:1218px !important;
	padding:200px 5px 200px 5px !important;
	background-color:rgba(255,255,255,0.1) !important;
}

a#backtotop:hover {
	border:2px solid #BFBFBF !important;
	background-color:rgba(255,255,255,0.8) !important;
	-webkit-box-shadow:2px 3px 5px 5px rgba(40,50,70,0.2);
	-moz-box-shadow:2px 3px 5px 5px rgba(40,50,70,0.2);
	box-shadow:2px 3px 5px 5px rgba(40,50,70,0.2);
}
/* кнопка перехода наверх --- конец */


/* визуальный редактор --- начало */
html.CSS1Compat, .cke_colorblock {
	background:#FFF !important;
}
/* визуальный редактор --- конец */


/* форма прикрипления файлов в ответах --- начало */
form#iframeUploadForm {
	padding:5px !important;
	background:#FFF !important;
}
/* форма прикрипления файлов в ответах --- конец */
/*[[res]]*/
}

options.value:
div#content > div.ipsBox.ipsVerticalTabbed.ipsLayout.ipsLayout_withleft.ipsLayout_tinyleft > div.ipsVerticalTabbed_content.ipsLayout_content {
	width:900px !important;
}

div#content,
div#footer_utilities {
	width:940px !important;
}

div#header_bar {
	padding:2px 0 0 0 !important;
	width:950px !important;
}

div#footer_utilities,
div#ipsDebug_footer {
	padding:10px !important;
	width:950px !important;
}

div#ipsDebug_footer {
	width:960px !important;
}

div#branding {
	width:960px !important;
}

div#primary_nav {
	width:960px !important;
}

div#primary_nav > div.main_width {
	width:100% !important;
}

div.post.entry-content > p > span > img.bbc_img {
	max-width:700px !important;
}

div#footer_utilities.main_width.clearfix.clear > a#backtotop {
	margin-left:978px !important;
	width:10px !important;
}

@-moz-document url-prefix() { 
		div#footer_utilities.main_width.clearfix.clear > a#backtotop {
		margin-left:650px !important;
	}
}
